CREATE PROCEDURE `stp_GER_ARMAZEM_OBTERPORFILTRO`(IN `DESCRICAO` varchar(120), IN `FILIAL` integer)
BEGIN
	#Routine body goes here...
SET @STOCK := CONCAT('%',LOWER(TRIM(DESCRICAO)),'%');

IF(FILIAL > 0) THEN
SELECT A.*, F.FIL_NOME_COMERCIAL FROM GER_ARMAZEM A
INNER JOIN SIS_EMPRESA F ON F.FIL_CODIGO = ARM_CODIGO_FILIAL
WHERE ARM_DESCRICAO LIKE @STOCK AND
ARM_CODIGO_FILIAL=FILIAL ORDER BY F.FIL_NOME_COMERCIAL, ARM_SIGLA;
ELSE
SELECT A.*, F.FIL_NOME_COMERCIAL FROM GER_ARMAZEM A
INNER JOIN SIS_EMPRESA F ON F.FIL_CODIGO = ARM_CODIGO_FILIAL
WHERE ARM_DESCRICAO LIKE @STOCK ORDER BY F.FIL_NOME_COMERCIAL, ARM_SIGLA;
END IF;
END
