-- Procedure structure for stp_GER_ARTIGO_DIMENSOES_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_ARTIGO_DIMENSOES_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_GER_ARTIGO_DIMENSOES_OBTERPORFILTRO`(IN `ARTIGO` int,IN `TAMANHO` int,IN `COR` int,IN `REFERENCIA` varchar(20),IN `CODIGO_BARRAS` varchar(50),IN `DESIGNACAO` varchar(120))
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA = CONCAT("
SELECT D.*, ART_DESIGNACAO, COR_DESCRICAO, TAM_DESCRICAO, ARM_DESCRICAO, CAT_DESCRICAO,
IFNULL(A.ART_PRECO_VENDA, 0) AS ART_PRECO_VENDA, ART_IMAGEM FROM GER_ARTIGO_DIMENSOES D
INNER JOIN GER_ARTIGO ON ART_CODIGO = DIM_CODIGO_ARTIGO
INNER JOIN GER_TAMANHO ON TAM_CODIGO = DIM_CODIGO_TAMANHO
INNER JOIN GER_CORES ON COR_CODIGO = DIM_CODIGO_COR 
INNER JOIN GER_ARMAZEM ON ARM_CODIGO = DIM_CODIGO_ARMAZEM
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA
INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = A.ART_UNIDADE_VENDA
WHERE ART_DESGINACAO LIKE '%",DESIGNACAO,"%' AND 
DIM_CODIGO_BARRAS LIKE '%",CODIGO_BARRAS,"%' AND
DIM_REFERENCIA LIKE '%",REFERENCIA,"%'
");
IF(ARTIGO > 0) THEN
SET vCONSULTA = CONCAT(vCONSULTA," AND DIM_CODIGO_ARTIGO =", ARTIGO); 
END IF;

IF(TAMANHO > 0) THEN
SET vCONSULTA = CONCAT(vCONSULTA," AND DIM_CODIGO_TAMANHO =", TAMANHO); 
END IF;

IF(COR > 0) THEN
SET vCONSULTA = CONCAT(vCONSULTA," AND DIM_CODIGO_COR =", COR); 
END IF;

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY ART_DESIGNACAO");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;


END
;;
delimiter ;

