-- ----------------------------
-- Procedure structure for stp_GER_ARTIGO_GENERATE_ALL_PRODUCTS_BARCODE
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_ARTIGO_GENERATE_ALL_PRODUCTS_BARCODE`;
delimiter ;;
CREATE PROCEDURE `stp_GER_ARTIGO_GENERATE_ALL_PRODUCTS_BARCODE`()
BEGIN
	#Routine body goes here... 
DECLARE PRODUCT_ID INT DEFAULT 0;
DECLARE done INT DEFAULT FALSE;
DECLARE BARCODE VARCHAR(50) DEFAULT "";


DECLARE PRODCUT_LIST CURSOR FOR SELECT ART_CODIGO FROM GER_ARTIGO;
DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

OPEN PRODCUT_LIST;


read_loop: LOOP


FETCH PRODCUT_LIST INTO PRODUCT_ID;
	IF done THEN
	 LEAVE read_loop;
	END IF;

IF(PRODUCT_ID < 10) THEN
  SET BARCODE = CONCAT("50521210",PRODUCT_ID);
ELSE IF(PRODUCT_ID >=10 AND PRODUCT_ID < 100) THEN
  SET BARCODE = CONCAT("5052121",PRODUCT_ID);
ELSE IF(PRODUCT_ID >=100 AND PRODUCT_ID < 1000) THEN
  SET BARCODE = CONCAT("505212",PRODUCT_ID);
ELSE IF(PRODUCT_ID >=1000 AND PRODUCT_ID < 10000)THEN
  SET BARCODE = CONCAT("50521",PRODUCT_ID);
ELSE IF(PRODUCT_ID >=10000 AND PRODUCT_ID < 100000) THEN
  SET BARCODE = CONCAT("5052",PRODUCT_ID);
ELSE IF(PRODUCT_ID >=100000 AND PRODUCT_ID < 100000) THEN
  SET BARCODE = CONCAT("505",PRODUCT_ID);
END IF;
END IF;
END IF;
END IF;
END IF;
END IF;

UPDATE GER_ARTIGO SET ART_CODIGO_BARRAS = BARCODE
WHERE ART_CODIGO = PRODUCT_ID AND (ART_CODIGO_BARRAS IS NULL OR ART_CODIGO_BARRAS="");

END LOOP;
END
;;
