-- ----------------------------
-- Procedure structure for stp_GER_ARTIGO_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_ARTIGO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_GER_ARTIGO_OBTERPORFILTRO`(IN `NOME` varchar(120),IN `CODIGO_BARRAS` varchar(120),IN FILIAL INT, IN REFERENCIA VARCHAR(120), CODIGO INT, CATEGORIA INT, DATA_INI DATE, DATA_TERM DATE, UTILIZADOR VARCHAR(20), ARMAZEM INT, PVP DECIMAL(18), PRODUCT_TYPE VARCHAR(1), IN CONVENIO_ID INT)
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;
DECLARE STOCK INT DEFAULT 0;
DECLARE ISROOT INT DEFAULT 0;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;

SELECT CAT_CATEGORIA INTO ISROOT FROM GER_CATEGORIA WHERE CAT_CODIGO = CATEGORIA;
SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;
SET vCONSULTA = CONCAT("
	SELECT DISTINCT
	A.ART_CODIGO,
	A.ART_CODIGO_BARRAS,
  A.ART_REFERENCIA,
  ",STOCK," AS STOCK,
	UPPER(A.ART_DESIGNACAO) AS ART_DESIGNACAO,
  IFNULL(CONCAT(SF.CAT_DESCRICAO, ' ', F.CAT_DESCRICAO), F.CAT_DESCRICAO) AS FAMILIA,
  IFNULL(A.ART_PRECO_VENDA, 0) AS ART_PRECO_VENDA,
  UPPER(UNI_SIGLA) AS UNI_SIGLA,
	A.ART_IMAGEM, 
	IMP_VALOR AS IMPOSTO,
	A.ART_MOVIMENTA_STOCK,
	IFNULL(A.ART_PRECO_CUSTO, 0) AS ART_PRECO_CUSTO,
	A.ART_CADASTRO,
  IMP_SIGLA,
  ART_VALIDADE,
  IFNULL(ART_IMPOSTO_INCLUIDO, 0) AS ART_IMPOSTO_INCLUIDO,
  IFNULL(ART_PEDIDO_COZINHA, 0) AS ART_PEDIDO_COZINHA,
  IFNULL(ART_DESCONTO, 0) AS ART_DESCONTO,
  IFNULL(ART_PCM_CALCULADO,0) AS ART_PCM,
  IFNULL(ART_DEVOLUCAO, 0) AS ART_DEVOLUCAO,
  IFNULL(ART_ALLOW_LOTE, 0) AS ART_ALLOW_LOTE,
  IFNULL(ART_ALLOW_DIMENSOES, 0) AS ART_ALLOW_DIMENSOES,
  IFNULL(ART_COMPOSTO, 0) AS ART_COMPOSTO,
  IFNULL(ART_TABELA_PRECO, 0) AS ART_TABELA_PRECO,
  IFNULL(ART_DESCONTINUADO, 0) AS ART_DESCONTINUADO,
  SF.CAT_DESCRICAO,
  IFNULL(STOCK_CODIGO_ARMAZEM, -1) AS ARMAZEM, ART_TIPO,
	IFNULL(IMP_CODIGO, -1) AS IMP_CODIGO,
  IFNULL(ART_VALOR_IMPOSTO,0) AS ART_VALOR_IMPOSTO, 
  IMP_DESCRICAO,    
  IFNULL(ART_POS, 0) AS ART_POS,
  IFNULL(ART_ONLY_ORDER, 0) AS ART_ONLY_ORDER,
  IFNULL(ART_RESTPOS, 0) AS ART_RESTPOS,
  IFNULL(ART_RETAIL_ID,0) AS ART_RETAIL_ID,
	IFNULL(ART_ALERTA_RUPTURA,0) AS ART_ALERTA_RUPTURA,
	IFNULL(ART_VASILHAME_ID,0) AS ART_VASILHAME_ID,
	IFNULL(ART_LOTE_MANAGMENT, 0) AS ART_LOTE_MANAGMENT,
	IFNULL(ART_ALLOW_RESERVE_STOCK, 0) AS ART_ALLOW_RESERVE_STOCK,
	IFNULL(ART_ALLOW_DOWN_STOCK,0) AS ART_ALLOW_DOWN_STOCK,
  IFNULL(ART_UNIDADE_REFERENCIA, -1) AS UNIDADE_ARMAZENAMENTO,
  IFNULL(ART_UNIDADE_ENTRADA, -1) AS UNIDADE_ENTRADA,
  IFNULL(ART_UNIDADE_SAIDA, -1) AS UNIDADE_SAIDA_STOCK,
  ART_UNIDADE_VENDA,  
  ARM_DESCRICAO, 
	ART_DESCRICAO_COMERCIAL, 
	IMP_TIPO,
	IFNULL(ART_RETENCAO_ID, 0) AS ART_RETENCAO_ID,
	ART_CURRENCY_ID,
	UPPER(MOE_SIGLA) AS MOE_SIGLA,
	ART_CUSTOMER_ID, 
	IFNULL(ART_TEMPO_PREPARACAO, 1) AS ART_TEMPO_PREPARACAO,
	IFNULL(ART_PRAZO_ENTREGA, 1) AS ART_PRAZO_ENTREGA,
	IFNULL(COB_PRECO_ACORDADO, 0) AS COB_PRECO_ACORDADO,
	IFNULL(COB_VALOR_PARCEIRO, 0) AS COB_VALOR_PARCEIRO,
  IFNULL(COB_VALOR_UTENTE, 0) AS COB_VALOR_UTENTE,
	IFNULL(ART_WIDTH, 0) AS ART_WIDTH, IFNULL(ART_HEIGHT, 0) AS ART_HEIGHT
 
FROM GER_ARTIGO A
LEFT JOIN GER_CATEGORIA F ON F.CAT_CODIGO = ART_CATEGORIA
LEFT JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = A.ART_UNIDADE_VENDA
LEFT JOIN GER_IMPOSTOS ON IMP_CODIGO = ART_CODIGO_IMPOSTO
LEFT JOIN GER_CATEGORIA SF ON SF.CAT_CODIGO = F.CAT_CATEGORIA
LEFT JOIN COM_STOCK ON STOCK_CODIGO_ARTIGO = ART_CODIGO
LEFT JOIN GER_ARMAZEM ON ARM_CODIGO = STOCK_CODIGO_ARMAZEM 
LEFT JOIN GER_ARTIGO_LOTE ON LOT_CODIGO_ARTIGO = ART_CODIGO
LEFT JOIN GER_ARTIGO_SERIAL_NUMBER ON SERIAL_ARTIGO_ID = ART_CODIGO
LEFT JOIN FIN_MOEDA ON MOE_CODIGO = ART_CURRENCY_ID
LEFT JOIN COM_CLIENTE_ARTIGO ON ART_PRODUCT_ID = ART_CODIGO
LEFT JOIN CLI_PLANO_CONVENIO_COBERTURA ON COB_CODIGO_SERVICO = ART_CODIGO
WHERE ART_STATUS !='E'");
IF(CODIGO > 0) THEN
SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CODIGO = ", CODIGO);
ELSE
  SET vCONSULTA = CONCAT(vCONSULTA,
  " AND (ART_DESIGNACAO LIKE '%",NOME,"%' OR ART_DESCRICAO_COMERCIAL LIKE '%",NOME,"%' OR ART_REFERENCIA LIKE '%",REFERENCIA,"%' OR ART_CODIGO_BARRAS LIKE '%",CODIGO_BARRAS,"%' OR LOT_REFERENCIA LIKE '%",NOME,"%' OR LOT_CODIGO_BARRAS LIKE '%",CODIGO_BARRAS,"%' OR SERIAL_SERIAL_NUMBER LIKE '%",NOME,"%' OR SERIAL_BARCODE LIKE '%",CODIGO_BARRAS,"%'");
    IF(PVP > 0) THEN
     SET vCONSULTA = CONCAT(vCONSULTA," OR ART_PRECO_VENDA LIKE '%",PVP,"%') ");
    ELSE
     SET vCONSULTA = CONCAT(vCONSULTA,") ");
    END IF;

    IF(ARMAZEM > 0) THEN
      SET vCONSULTA = CONCAT(vCONSULTA," AND (STOCK_CODIGO_ARMAZEM=", ARMAZEM, " OR ART_MOVIMENTA_STOCK=0 )");
    ELSE
      SET vCONSULTA = CONCAT(vCONSULTA," AND (ART_MOVIMENTA_STOCK=0 OR STOCK_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID 	FROM GER_ARMAZEM_PERMISSOES
       WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,")))");
    END IF;
   IF(FILIAL > 0) THEN
    SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CODIGO_FILIAL = ",FILIAL);
   ELSE
    SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CODIGO_FILIAL IN (SELECT UTI_FILIAL_CODIGO_FILIAL FROM SIS_UTILIZADOR_FILIAL WHERE UTI_FILIAL_UTILIZADOR ='",UTILIZADOR,"')");
   END IF;

    IF(CATEGORIA > 0) THEN
     
    SET vCONSULTA = CONCAT(vCONSULTA," AND (ART_CATEGORIA IN (SELECT CAT_CODIGO FROM GER_CATEGORIA WHERE CAT_CATEGORIA = ", CATEGORIA, ") OR ART_CATEGORIA =", CATEGORIA," )"); 
	 END IF;

	IF(DATA_INI IS NOT NULL  AND DATA_TERM IS NOT NULL) THEN
	 SET vCONSULTA =CONCAT(vCONSULTA," AND  (ART_VALIDADE BETWEEN  '",DATA_INI, "' AND '",DATA_TERM, "' 
	 OR LOT_DATA_VALIDADE BETWEEN  '",DATA_INI, "' AND '",DATA_TERM, "')"); 
	END IF; 
	
	IF(PRODUCT_TYPE IS NOT NULL AND PRODUCT_TYPE!="") THEN
		 SET vCONSULTA = CONCAT(vCONSULTA, " AND ART_TIPO ='",PRODUCT_TYPE,"'");
	END IF;

	IF(CONVENIO_ID > 0) THEN
   SET vCONSULTA = CONCAT(vCONSULTA, " AND COB_CODIGO_CONVENIO = ",CONVENIO_ID); 
	END IF;
END IF;



#SELECT vCONSULTA;

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY ART_CODIGO ");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
