BEGIN
	#Routine body goes here...
DECLARE ART_DATA_CONTAGEM DATETIME DEFAULT NULL; 

SELECT MAX(STOCK_DATA_ULTIMA_CONTAGEM) INTO ART_DATA_CONTAGEM FROM COM_STOCK WHERE STOCK_CODIGO_ARTIGO = CODIGO;
SELECT
	ART_CODIGO,
	ART_CODIGO_BARRAS,
	ART_REFERENCIA,
	ART_TIPO,
	ART_DESIGNACAO,
	IFNULL(ART_CATEGORIA, -1) AS ART_CATEGORIA,
