-- ----------------------------
-- Procedure structure for stp_GER_AUTO_MARCACAO_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_AUTO_MARCACAO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_GER_AUTO_MARCACAO_OBTERPORFILTRO`(IN `CODIGO` int,IN `DE` date,IN `ATE` date,IN `MATRICULA` varchar(20),IN `CAR_ID` int,IN `ENTIDADE` varchar(120),IN `ENTIDADE_ID` int,IN `PHONE_NUMBER` varchar(20),IN `ESTADO` int,IN `DOCUMENT_ID` int,IN `NUMBER` int,IN `UTILIZADOR` varchar(20))
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
 
SET v_CON_ = CONCAT("
SELECT M.*, B.ENT_NOME_COMPLETO, ART_DESIGNACAO, CONCAT(V.MAR_DESCRICAO, ' ', T.MAR_DESCRICAO, ' MATRÍCULA: ', VIA_LICENSE_ID) AS VEICULO,
C.ENT_NOME_COMPLETO AS PROPRIETARIO, C.ENT_TELEFONE, C.ENT_EMAIL FROM AUTO_MARCACAO M
INNER JOIN GER_VIATURA ON VIA_CODIGO = M.MAR_CAR_ID
INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = VIA_OWNER_ID
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = MAR_DOCUMENT_ID
INNER JOIN GER_ENTIDADE B ON B.ENT_CODIGO = MAR_BILLING_ENTITY_ID
INNER JOIN GER_ARTIGO ON ART_CODIGO = MAR_SERVICE_ID
INNER JOIN GER_MARCA T ON T.MAR_CODIGO = VIA_MARCA_ID
LEFT JOIN GER_MARCA V ON V.MAR_CODIGO = T.MAR_ROOT  ");
IF(CODIGO > 0) THEN
SET v_CON_ = CONCAT(v_CON_," WHERE MAR_CODIGO =",CODIGO);
ELSE IF(CAR_ID > 0) THEN
SET v_CON_ = CONCAT(v_CON_," WHERE MAR_CAR_ID =",CAR_ID); 
ELSE IF(ENTIDADE_ID > 0) THEN

SET v_CON_ = CONCAT(v_CON_," WHERE (MAR_BILLING_ENTITY_ID =",ENTIDADE_ID, " OR VIA_OWNER_ID =", ENTIDADE_ID,") ");

ELSE
SET v_CON_ = CONCAT(v_CON_," WHERE 1=1 AND VIA_LICENSE_ID LIKE '%",MATRICULA,"%'");

IF(DE IS NOT NULL  AND ATE IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND DATE(MAR_CHECK_IN) BETWEEN '",DE, "' AND '",ATE, "'");
	ELSE IF(DE IS NOT NULL AND ATE IS NULL )
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND MAR_CHECK_IN >='",DE, "'");
	ELSE IF(DE IS NULL  AND ATE IS NOT NULL)
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND MAR_CHECK_IN <= '",ATE, "'");
	END IF;
	END IF;
	END IF;
END IF;
END IF;
END IF;  


SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

