DROP PROCEDURE IF EXISTS `stp_GER_ENTIDADE_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_GER_ENTIDADE_ADICIONAR`(IN `CODIGO` int,IN `RAZAO` varchar(120),IN `FANTASIA` varchar(120),IN `CATEGORIA`INT,IN `ENTIDADE` varchar(1),IN `IDENTIFICACAO` varchar(18),IN `PAIS` int,IN `RUA` varchar(120),IN `BAIRRO` varchar(120),IN `MORADA` int,IN `TELEFONE` varchar(19),IN `TELF_ALT` varchar(19),IN `FAX` varchar(19),IN `EMAIL` varchar(120),IN `WEBSITE` varchar(120), IN DESCONTO DECIMAL(18,0), LIMITE_CREDITO DECIMAL(18,0), IN FILIAL INT, IN UTILIZADOR VARCHAR(20), IN DISTRITO VARCHAR(50), IN CAIXA_POSTAL VARCHAR(18), OUT LSTID INT)
BEGIN
	#Routine body goes here...
	
IF(CODIGO =0) THEN

SELECT IFNULL(ENT_CODIGO,0) INTO CODIGO FROM GER_ENTIDADE WHERE ENT_NOME_COMPLETO = RAZAO AND ENT_TELEFONE = TELEFONE;
END IF;


IF(CODIGO <=0) THEN

  INSERT INTO GER_ENTIDADE (ENT_NOME_COMPLETO, ENT_CODIGO_PAIS, ENT_LOCAL_MORADA, ENT_RUA, ENT_BAIRRO, ENT_TELEFONE, 
  ENT_TELEFONE_ALTERNATIVO, ENT_TELEFONE_FAX, ENT_EMAIL, ENT_WEBSITE, ENT_STATUS, ENT_DATA_INCLUSAO, ENT_IDENTIFICACAO, ENT_DISTRITO, ENT_CAIXA_POSTAL)
	VALUES (RAZAO, PAIS, MORADA, RUA, BAIRRO, TELEFONE, TELF_ALT, FAX, EMAIL, WEBSITE, 1, NOW(), IDENTIFICACAO, DISTRITO,
	CAIXA_POSTAL);
	SELECT LAST_INSERT_ID() INTO CODIGO;

  IF(ENTIDADE="B") THEN
    INSERT INTO FIN_BANCO(BANC_CODIGO, BANC_SIGLA, BANC_CODIGO_FILIAL) 
		VALUES(CODIGO, FANTASIA, FILIAL); 
  ELSE /*IF(ENTIDADE!="F") THEN*/  
	 IF(NOT EXISTS(SELECT * FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = CODIGO)) THEN
   INSERT INTO GER_TERCEIROS (TER_CODIGO_ENTIDADE, TER_CODIGO_CATEGORIA, TER_CODIGO_FILIAL, TER_NOME_COMERCIAL, 
   TER_TIPO, TER_LIMITE_CREDITO, TER_DESCONTO, TER_SALDO, TER_CREATED_BY, TER_CREATED_DATE, TER_UPDATED_BY, TER_UPDATED_DATE)
   VALUES(CODIGO, CATEGORIA, FILIAL, FANTASIA, ENTIDADE, LIMITE_CREDITO, DESCONTO, 0, UTILIZADOR, NOW(), UTILIZADOR, NOW());
	 END IF;
  /*END IF;*/ 
  END IF;
ELSE
	
  UPDATE GER_ENTIDADE SET 
	ENT_NOME_COMPLETO = RAZAO, 
	ENT_CODIGO_PAIS= PAIS,
	ENT_LOCAL_MORADA = MORADA,
  ENT_RUA = RUA, ENT_BAIRRO = BAIRRO,
	ENT_TELEFONE=TELEFONE,
	ENT_TELEFONE_ALTERNATIVO=TELF_ALT, 
	ENT_TELEFONE_FAX= FAX,
  ENT_WEBSITE = WEBSITE,
	ENT_EMAIL=EMAIL,
  ENT_IDENTIFICACAO = IDENTIFICACAO,
  ENT_DISTRITO = DISTRITO,
	ENT_CAIXA_POSTAL = CAIXA_POSTAL
	WHERE ENT_CODIGO = CODIGO;
  
   IF(ENTIDADE="B") THEN
      IF(EXISTS(SELECT * FROM FIN_BANCO WHERE BANC_CODIGO = CODIGO))THEN
          UPDATE FIN_BANCO SET 
					BANC_SIGLA = FANTASIA
					WHERE BANC_CODIGO = CODIGO;
      ELSE
			   IF(NOT EXISTS(SELECT * FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = CODIGO)) THEN
           INSERT INTO GER_TERCEIROS (TER_CODIGO_ENTIDADE, TER_CODIGO_CATEGORIA, TER_CODIGO_FILIAL)
					 VALUES(CODIGO, CATEGORIA, FILIAL);
				 END IF;
      END IF;         
   ELSE IF(ENTIDADE!="F") THEN
     IF(EXISTS(SELECT * FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = CODIGO)) THEN
        UPDATE GER_TERCEIROS SET
        TER_CODIGO_CATEGORIA = CATEGORIA,
	 			TER_TIPO = ENTIDADE,
  			TER_NOME_COMERCIAL = FANTASIA,
        TER_LIMITE_CREDITO = LIMITE_CREDITO,
        TER_DESCONTO = DESCONTO
       WHERE TER_CODIGO_ENTIDADE = CODIGO;
     ELSE
        IF(NOT EXISTS(SELECT * FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = CODIGO)) THEN
				  INSERT INTO GER_TERCEIROS (TER_CODIGO_ENTIDADE, TER_CODIGO_CATEGORIA, TER_CODIGO_FILIAL, TER_NOME_COMERCIAL, 	TER_TIPO)
   VALUES(CODIGO, CATEGORIA, FILIAL, FANTASIA, ENTIDADE);
				END IF;
     END IF;
   END IF; 
   END IF;
     
  
END IF;
SET LSTID:=CODIGO;
END
;;
delimiter ;

