-- ----------------------------
-- Procedure structure for stp_GER_ENTIDADE_OBTEREMAIL
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_ENTIDADE_OBTEREMAIL`;
delimiter ;;
CREATE PROCEDURE `stp_GER_ENTIDADE_OBTEREMAIL`(IN `FILIAL` int,IN `UNIDADE` VARCHAR(10),IN `CURSO` int,IN `CLASSE` int,IN `TURMA` int,IN `DISCIPLINA` int,IN `DESTINATARIO` varchar(1),IN `ANO` int)
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;
DECLARE vWHERE VARCHAR(120);

IF(DESTINATARIO='D') THEN #OBTER EMAIL DOS DOCENTES
SET vCONSULTA = CONCAT("
SELECT DISTINCT ENT_NOME_COMPLETO, LOWER(ENT_EMAIL) AS ENT_EMAIL, '' AS ALU_ENCARREGADO FROM ACA_DOCENTE
LEFT JOIN  GER_ENTIDADE  ON ENT_CODIGO = DOC_CODIGO_FUNCIONARIO ");

IF(UNIDADE IS NOT NULL AND UNIDADE!='' AND UNIDADE !='-1' AND UNIDADE !='0' OR CURSO > 0 OR  CLASSE > 0 OR TURMA > 0 OR  DISCIPLINA >0) THEN
		SET vCONSULTA = CONCAT(vCONSULTA,"LEFT JOIN ACA_HORARIO ON HOR_CODIGO_DOCENTE = DOC_CODIGO_FUNCIONARIO
		LEFT JOIN ACA_PLANO_CURRICULAR_DISCIPLINA ON DIS_PLAN_CODIGO = HOR_DISCIPLINA
		LEFT JOIN ACA_PLANO_CURRICULAR ON PLAN_CODIGO = DIS_PLAN_CODIGO_PLANO
		LEFT JOIN ACA_CURSO ON CUR_CODIGO = PLAN_CODIGO_RAMO
		LEFT JOIN ACA_COORDENACAO ON COOR_SIGLA = CUR_ESPECIFICACAO
		LEFT JOIN ACA_TURMA ON TUR_CODIGO = HOR_TURMA 
    WHERE DOC_ESTADO =1 AND ENT_EMAIL IS NOT NULL AND ENT_EMAIL!='' ORDER BY ENT_NOME_COMPLETO
		");

		IF(UNIDADE IS NOT NULL AND UNIDADE!='' AND UNIDADE !='-1' AND UNIDADE !='0') THEN
		SET vCONSULTA = CONCAT(vCONSULTA, " AND CUR_ESPECIFICACAO= '", UNIDADE, "'");
		END IF;

		IF(CURSO > 0 ) THEN
		SET vCONSULTA = CONCAT(vCONSULTA, " AND CUR_CODIGO = ", CURSO);
		END IF;

		IF(CLASSE > 0) THEN
		SET vCONSULTA = CONCAT(vCONSULTA, " AND PLAN_CODIGO = ", CLASSE);
		END IF;

		IF(TURMA > 0) THEN
		SET vCONSULTA = CONCAT(vCONSULTA, "  AND HOR_TURMA = ", TURMA);

		END IF;

		IF(DISCIPLINA >0) THEN
		SET vCONSULTA = CONCAT(vCONSULTA, " AND HOR_DISCIPLINA = ", DISCIPLINA);
		END IF;

		IF(ANO > 0) THEN
		SET vCONSULTA = CONCAT(vCONSULTA, " AND DIS_PLAN_ANO_LECTIVO = ", ANO);
		END IF;
ELSE
 SET vCONSULTA = CONCAT(vCONSULTA, "WHERE DOC_ESTADO =1 AND ENT_EMAIL IS NOT NULL AND ENT_EMAIL!='' ORDER BY ENT_NOME_COMPLETO");
END IF;

ELSE IF(DESTINATARIO='A') THEN #OBTER EMAIL DOS ALUNOS

	SET vCONSULTA = CONCAT("SELECT ENT_NOME_COMPLETO, LOWER(ENT_EMAIL) AS ENT_EMAIL, LOWER(ALU_ENCARREGADO_EMAIL) AS ALU_ENCARREGADO_EMAIL FROM GER_ENTIDADE
	INNER JOIN ACA_ALUNO ON ALU_CODIGO = ENT_CODIGO
	INNER JOIN ACA_MATRICULA ON MAT_CODIGO_ALUNO = ALU_CODIGO
	INNER JOIN ACA_PLANO_CURRICULAR ON PLAN_CODIGO = MAT_CODIGO_PLANO_CURRICULAR
	INNER JOIN ACA_CURSO ON CUR_CODIGO = PLAN_CODIGO_RAMO
	INNER JOIN ACA_TURMA_ALUNO TA ON TA.TUR_ALU_CODIGO_ALUNO = MAT_CODIGO_ALUNO
	INNER JOIN ACA_PLANO_CURRICULAR_DISCIPLINA ON DIS_PLAN_CODIGO_PLANO = PLAN_CODIGO
	LEFT JOIN ACA_COORDENACAO ON COOR_SIGLA = CUR_ESPECIFICACAO 
	WHERE ENT_EMAIL IS NOT NULL AND ENT_EMAIL!='' OR ALU_ENCARREGADO_EMAIL IS NOT NULL 
	AND ALU_ENCARREGADO_EMAIL !='' AND MAT_CODIGO_FILIAL = FILIAL ");
	IF(UNIDADE IS NOT NULL AND UNIDADE!='') THEN
	SET vCONSULTA = CONCAT(vCONSULTA, " AND CUR_ESPECIFICACAO= '", UNIDADE, "'");
	END IF;

	IF(CURSO > 0 ) THEN
	SET vCONSULTA = CONCAT(vCONSULTA, " AND CUR_CODIGO = ", CURSO);
	END IF;

	IF(CLASSE > 0) THEN
	SET vCONSULTA = CONCAT(vCONSULTA, " AND MAT_CODIGO_PLANO_CURRICULAR = ", CLASSE);
	END IF;

	IF(TURMA > 0) THEN
	SET vCONSULTA = CONCAT(vCONSULTA, "  AND TA.TUR_ALU_CODIGO_TURMA = ", TURMA);

	END IF;

	IF(DISCIPLINA >0) THEN
	SET vCONSULTA = CONCAT(vCONSULTA, " AND DIS_PLAN_CODIGO = ", DISCIPLINA);
	END IF;

	IF(ANO > 0) THEN
	SET vCONSULTA = CONCAT(vCONSULTA, " AND MAT_CODIGO_ANO = ", ANO);
	END IF;

ELSE #OBTER EMAIL DE TODOS FUNCIONÁRIOS

	SET vCONSULTA = CONCAT("SELECT '' AS ENT_NOME_COMPLETO, '' AS ENT_EMAIL, '' AS ALU_ENCARREGADO_EMAIL");

	END IF;
END IF;


SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_GER_ENTIDADE_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_ENTIDADE_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_GER_ENTIDADE_OBTERPORFILTRO`(IN `NOME` varchar(120))
BEGIN
	#Routine body goes here...
SET @FILTRO := CONCAT('%',LOWER(TRIM(NOME)),'%');
 
SELECT ENT_CODIGO, ENT_IDENTIFICACAO, ENT_NOME_COMPLETO, CONCAT(UPPER(ENT_BAIRRO)," ", UPPER(ENT_RUA)) AS MORDADA, 
ENT_TELEFONE, ENT_EMAIL, ENT_WEBSITE, ENT_TELEFONE_ALTERNATIVO, TER_TIPO, GRU_DESCRICAO,

IFNULL((
 SELECT 
	 
  IFNULL((SUM(FAT_VALOR_TOTAL - FAT_VALOR_PAGO)+SUM(FAT_TROCO)),0) AS  FAT_VALOR_SALDO
 
  
FROM 
	COM_FATURA_CLIENTE C 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE  FAT_STATUS = 1 AND FAT_CODIGO_CLIENTE = ENT_CODIGO
AND DOC_FORMATO IN ('INVOICE_R', 'INVOICE') AND FAT_STATUS_DOCUMENTO=8
GROUP BY FAT_CODIGO_CLIENTE), "0,00") AS SALDO, TER_CODIGO, 
TER_DESCONTO, TER_PAYMENT_METHOD_ID, TER_PAYMENT_TERMS_ID, TER_LINE_DISCOUNT, TER_LIMITE_CREDITO, TER_TABLE_PRICE_ID,
ENT_DATA_INCLUSAO, IFNULL(TER_CODIGO_FILIAL, "-1") AS TER_CODIGO_FILIAL, TER_CUSTOMER_IVA_ID, TER_COMPANY_INSURANCE
FROM GER_ENTIDADE 
LEFT JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
LEFT JOIN GER_GRUPO_CLIENTES ON GRU_CODIGO = TER_CODIGO_CATEGORIA
LEFT JOIN FIN_BANCO ON BANC_CODIGO = ENT_CODIGO 
WHERE TER_CODIGO > 0 AND ENT_NOME_COMPLETO LIKE @FILTRO
ORDER BY ENT_NOME_COMPLETO, ENT_CODIGO;

END
;;
