	#Routine body goes here...
SELECT ENT_CODIGO, ENT_NOME_COMPLETO, IFNULL(TER_NOME_COMERCIAL, BANC_SIGLA), IFNULL(TER_CODIGO_CATEGORIA, -1) AS CATEGORIA, 
ENT_IDENTIFICACAO, ENT_CODIGO_PAIS, ENT_RUA, ENT_BAIRRO, IFNULL(PROV_CODIGO, -1) AS PROVINCIA, IFNULL(ENT_LOCAL_MORADA, -1) AS MUNICIPIO,
ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, ENT_EMAIL, ENT_WEBSITE, TER_TIPO, IFNULL(TER_DESCONTO,0) AS TER_DESCONTO, 
IFNULL(TER_LIMITE_CREDITO,0) AS TER_LIMITE_CREDITO, ENT_DISTRITO
FROM GER_ENTIDADE
LEFT JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
LEFT JOIN FIN_BANCO ON BANC_CODIGO = ENT_CODIGO
LEFT JOIN GER_MUNICIPIO ON MUN_CODIGO = ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA ON PROV_CODIGO = MUN_CODIGO_PROVINCIA 
WHERE ENT_CODIGO = CODIGO;
END
