SELECT ENT_CODIGO, ENT_IDENTIFICACAO, ENT_NOME_COMPLETO, CONCAT(UPPER(ENT_RUA), ", ", UPPER(ENT_BAIRRO)) AS MORDADA, ENT_TELEFONE, ENT_EMAIL, ENT_WEBSITE,
ENT_TELEFONE_ALTERNATIVO FROM GER_ENTIDADE 
LEFT JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
LEFT JOIN FIN_BANCO ON BANC_CODIGO = ENT_CODIGO
WHERE ENT_NOME_COMPLETO LIKE @FILTRO AND TER_CODIGO_FILIAL = FILIAL
