BEGIN
	#Routine body goes here...
SET @FILTRO := CONCAT('%',LOWER(TRIM(DESCRICAO)),'%');

IF(SIGLA IS NOT NULL AND SIGLA='STA_CONS') THEN
  SELECT * FROM GER_STATUS WHERE (STA_SIGLA = SIGLA OR STA_SIGLA="" OR STA_SIGLA IS NULL) AND STA_STATUS='1'
  order by STA_DESCRICAO; 
ELSE IF(SIGLA IS NOT NULL AND SIGLA<>'STA_CONS' AND SIGLA<>'') THEN
  SELECT * FROM GER_STATUS WHERE STA_SIGLA = SIGLA AND STA_STATUS='1'
  order by STA_DESCRICAO;
ELSE
  SELECT * FROM GER_STATUS WHERE STA_DESCRICAO 
	LIKE @FILTRO AND STA_STATUS='1' order by STA_DESCRICAO;
END IF;
END IF;
END
;;
