-- Procedure structure for stp_GER_TASK_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_TASK_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_GER_TASK_OBTERPORFILTRO`(IN CODIGO INT, `SCHEDULE_FROM` datetime,`SCHEDULE_UNTIL` datetime, IN COMPANY_ID INT)
BEGIN
DECLARE v_CON_ LONGTEXT;
	#Routine body goes here...
IF(CODIGO > 0) THEN
	  SELECT T.*, U.UTI_NOME AS TASK_OWNER_NAME, E.ENT_NOME_COMPLETO AS EXECUTOR_NAME, ACT_DESCRICAO,    TRGT.ENT_NOME_COMPLETO
	AS TARGET_NAME  FROM GER_TASK T
	INNER JOIN SIS_UTILIZADOR U ON U.UTI_CODIGO = TASK_OWNER_USER_ID 
	INNER JOIN GER_ENTIDADE E ON E.ENT_CODIGO = TASK_EXECUTOR_ID
	INNER JOIN GER_TIPO_ACTIVIDADE ON ACT_CODIGO = TASK_ACTIVIDADE_ID
	INNER JOIN GER_ENTIDADE TRGT ON TRGT.ENT_CODIGO = TASK_TARGET_ID
  WHERE TASK_COMPANY_ID = COMPANY_ID AND TASK_CODIGO = CODIGO;
ELSE

SELECT T.*, U.UTI_NOME AS TASK_OWNER_NAME, E.ENT_NOME_COMPLETO AS EXECUTOR_NAME, ACT_DESCRICAO,    TRGT.ENT_NOME_COMPLETO
AS TARGET_NAME  FROM GER_TASK T
INNER JOIN SIS_UTILIZADOR U ON U.UTI_CODIGO = TASK_OWNER_USER_ID 
INNER JOIN GER_ENTIDADE E ON E.ENT_CODIGO = TASK_EXECUTOR_ID
INNER JOIN GER_TIPO_ACTIVIDADE ON ACT_CODIGO = TASK_ACTIVIDADE_ID
INNER JOIN GER_ENTIDADE TRGT ON TRGT.ENT_CODIGO = TASK_TARGET_ID
WHERE TASK_COMPANY_ID = COMPANY_ID AND TASK_SCHEDULE_DATE	BETWEEN SCHEDULE_FROM AND SCHEDULE_UNTIL;
	
END IF;

 
END
;;
