DROP PROCEDURE IF EXISTS `stp_GER_TECNICO_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_GER_TECNICO_ADICIONAR`(IN `ENTIDADE` int, IN COMISSAO int, IN `FILIAL` int, IN `GRUPO` int, VALOR_COMISSAO decimal(18,2), USERNAME varchar(20), UTILIZADOR varchar(20), NOME_TECNICO varchar(120), SENHA varchar(200), PERFIL INT, EMAIL VARCHAR(120), TIPO VARCHAR(1))
BEGIN
	#Routine body goes here...
	
IF(COMISSAO > 0) THEN
 SELECT COM_COMISSAO INTO VALOR_COMISSAO FROM REST_COMISSAO_VENDA WHERE COM_CODIGO = COMISSAO; 
END IF;

IF(NOT EXISTS(SELECT * FROM REST_FUNCIONARIOS WHERE GAR_CODIGO_ENTIDADE = ENTIDADE)) THEN

	INSERT INTO REST_FUNCIONARIOS
  (
      GAR_CODIGO,
			GAR_CODIGO_ENTIDADE,
			GAR_UTILIZADOR,
			GAR_STATUS,
			GAR_FILIAL,
			GAR_COMISSAO,
			GAR_VALOR_COMISSAO,
			GAR_COMISSAO_VENDA,
			GAR_COMISSAO_LIQUIDACAO,
			GAR_GROUP_ID,
			GAR_GROUP_DESIGNATION,
      GAR_TIPO, 
			GAR_CREATED_BY,
			GAR_CREATED_DATE,
			GAR_UPDATED_BY,
			GAR_UPDATED_DATE 
	)
	VALUES(DEFAULT, ENTIDADE, USERNAME, 'A', FILIAL, COMISSAO, VALOR_COMISSAO, 0, 0, GRUPO, NOME_TECNICO, TIPO, UTILIZADOR, NOW(), UTILIZADOR, NOW());

 

ELSE

UPDATE REST_FUNCIONARIOS SET
GAR_FILIAL = FILIAL,
GAR_COMISSAO = COMISSAO,
GAR_VALOR_COMISSAO = VALOR_COMISSAO,
GAR_GROUP_ID = GRUPO,
GAR_GROUP_DESIGNATION = NOME_TECNICO,
GAR_TIPO = TIPO, 
GAR_UPDATED_BY = UTILIZADOR,
GAR_UPDATED_DATE = NOW() 
WHERE GAR_CODIGO_ENTIDADE = ENTIDADE;

END IF;

END
;;
delimiter ;

