delimiter ;;
CREATE PROCEDURE `stp_GER_TECNICO_OBTERPORFILTRO`(IN `NOME` varchar(120),IN `FILIAL` int)
BEGIN
	#Routine body goes here...
SET @FILTRO := CONCAT('%',LOWER(TRIM(NOME)),'%');
IF(FILIAL > 0) THEN

SELECT ENT_CODIGO, ENT_NOME_COMPLETO, ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, IFNULL(COM_COMISSAO, 0) AS COMISSAO, ENT_EMAIL,
GAR_TIPO, GAR_CODIGO
FROM  REST_FUNCIONARIOS
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE 
LEFT JOIN REST_COMISSAO_VENDA ON COM_CODIGO = GAR_COMISSAO
WHERE ENT_NOME_COMPLETO LIKE @FILTRO AND GAR_FILIAL = FILIAL
ORDER BY ENT_NOME_COMPLETO, ENT_CODIGO;
ELSE

SELECT ENT_CODIGO, ENT_NOME_COMPLETO, ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, IFNULL(COM_COMISSAO, 0) AS COMISSAO, 
ENT_EMAIL, GAR_TIPO, GAR_CODIGO 
FROM  REST_FUNCIONARIOS
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE 
LEFT JOIN REST_COMISSAO_VENDA ON COM_CODIGO = GAR_COMISSAO
WHERE ENT_NOME_COMPLETO LIKE @FILTRO
ORDER BY ENT_NOME_COMPLETO, ENT_CODIGO;

END IF;
END
;;
