DROP PROCEDURE IF EXISTS `stp_GER_TERCEIRO_GETCUSTOMERLIST`;
delimiter ;;
CREATE PROCEDURE `stp_GER_TERCEIRO_GETCUSTOMERLIST`(IN `NOME` varchar(120))
BEGIN
	#Routine body goes here...
SET @FILTRO := CONCAT('%',LOWER(TRIM(NOME)),'%');
 
SELECT ENT_CODIGO, ENT_IDENTIFICACAO, ENT_NOME_COMPLETO, CONCAT(UPPER(ENT_BAIRRO)," ", UPPER(ENT_RUA)) AS MORDADA, 
ENT_TELEFONE, ENT_EMAIL, ENT_WEBSITE, ENT_TELEFONE_ALTERNATIVO, TER_TIPO, GRU_DESCRICAO,

"0,00" AS SALDO, TER_CODIGO_ENTIDADE, TER_DESCONTO, TER_PAYMENT_METHOD_ID, TER_PAYMENT_TERMS_ID,
TER_LINE_DISCOUNT, TER_LIMITE_CREDITO, TER_TABLE_PRICE_ID, IFNULL(TER_CREATED_DATE, ENT_DATA_INCLUSAO) AS ENT_DATA_INCLUSAO,
IFNULL(TER_CODIGO_FILIAL, "-1") AS TER_CODIGO_FILIAL, IFNULL(TER_CURRENCY_ID, -1) AS TER_CURRENCY_ID, TER_PREFERENCIAL_CONTACT,
TER_HOW_FINDING_US, TER_BIRTH_DATE, TER_CODIGO, IFNULL(TER_RETENCAO, -1) AS TER_RETENCAO, IFNULL(TER_STATUS, 1) AS TER_STATUS, IFNULL(TER_CUSTOMER_IVA_ID, -1) AS TER_CUSTOMER_IVA_ID

FROM GER_ENTIDADE 
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
LEFT JOIN GER_GRUPO_CLIENTES ON GRU_CODIGO = TER_CODIGO_CATEGORIA
LEFT JOIN FIN_BANCO ON BANC_CODIGO = ENT_CODIGO 
WHERE (TER_TIPO IN ('C', '', 'A') OR TER_TIPO IS NULL) AND (ENT_NOME_COMPLETO LIKE CONCAT('%',LOWER(TRIM(NOME)),'%') OR ENT_TELEFONE LIKE CONCAT('%',LOWER(TRIM(NOME)),'%') OR ENT_IDENTIFICACAO LIKE CONCAT('%',LOWER(TRIM(NOME)),'%') OR ENT_EMAIL LIKE CONCAT('%',LOWER(TRIM(NOME)),'%')
OR ENT_TELEFONE LIKE CONCAT('%',LOWER(TRIM(NOME)),'%'))
ORDER BY TER_CODIGO, ENT_NOME_COMPLETO;

END
;;
