-- Procedure structure for stp_GER_TERCEIRO_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_GER_TERCEIRO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_GER_TERCEIRO_OBTERPORFILTRO`(IN `NOME` varchar(120))
BEGIN
	#Routine body goes here...
SET @FILTRO := CONCAT('%',LOWER(TRIM(NOME)),'%');
 
SELECT TER_CODIGO, ENT_IDENTIFICACAO, ENT_NOME_COMPLETO, CONCAT(UPPER(ENT_BAIRRO)," ", UPPER(ENT_RUA)) AS MORDADA, 
ENT_TELEFONE, ENT_EMAIL, ENT_WEBSITE, ENT_TELEFONE_ALTERNATIVO, TER_TIPO, GRU_DESCRICAO,

IFNULL((
 SELECT 
	 
  IFNULL((SUM(FAT_VALOR_TOTAL - FAT_VALOR_PAGO)+SUM(FAT_TROCO)),0) AS  FAT_VALOR_SALDO
 
  
FROM 
	COM_FATURA_CLIENTE C 
WHERE  FAT_STATUS = 1 AND FAT_CODIGO_CLIENTE = ENT_CODIGO
GROUP BY FAT_CODIGO_CLIENTE), "0,00") AS SALDO, TER_CODIGO_ENTIDADE, TER_DESCONTO, TER_PAYMENT_METHOD_ID, TER_PAYMENT_TERMS_ID,
TER_LINE_DISCOUNT, TER_LIMITE_CREDITO, TER_TABLE_PRICE_ID, ENT_DATA_INCLUSAO, TER_CODIGO_FILIAL, TER_COMPANY_INSURANCE,
TER_PREFERENCIAL_CONTACT, TER_HOW_FINDING_US, TER_BIRTH_DATE, IFNULL(TER_CUSTOMER_IVA_ID, -1) AS TER_CUSTOMER_IVA_ID, 
IFNULL(TER_SUPPLIER_IVA_ID, -1) AS TER_SUPPLIER_IVA_ID

FROM GER_ENTIDADE 
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
INNER JOIN GER_GRUPO_CLIENTES ON GRU_CODIGO = TER_CODIGO_CATEGORIA
LEFT JOIN FIN_BANCO ON BANC_CODIGO = ENT_CODIGO 
WHERE ENT_NOME_COMPLETO LIKE @FILTRO
ORDER BY ENT_NOME_COMPLETO, ENT_CODIGO;

END
;;
