DROP PROCEDURE IF EXISTS `stp_GER_TERCEIRO_OBTERPORPK`;
delimiter ;;
CREATE PROCEDURE `stp_GER_TERCEIRO_OBTERPORPK`(IN `CODIGO` int)
BEGIN
	#Routine body goes here...
DECLARE ALLOWED_DELETE_NIF BIT DEFAULT 1; 

IF(EXISTS(SELECT FAT_CODIGO FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO_CLIENTE = CODIGO)
/*OR EXISTS(SELECT * FROM COM_RECIBO_CLIENTE WHERE REC_CODIGO_ENTIDADE = CODIGO)*/) THEN  
	SET ALLOWED_DELETE_NIF = 0; 
END IF;



SELECT T.*,  IFNULL(TER_NOME_COMERCIAL, BANC_SIGLA) AS COMERCIAL_NAME, IFNULL(TER_CODIGO_CATEGORIA, -1) AS CATEGORIA, 
ENT_IDENTIFICACAO, ENT_CODIGO_PAIS, ENT_RUA, ENT_BAIRRO, IFNULL(PROV_CODIGO, -1) AS PROVINCIA, IFNULL(ENT_LOCAL_MORADA, -1) AS MUNICIPIO, ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, ENT_EMAIL, ENT_WEBSITE, ENT_DISTRITO,
ALLOWED_DELETE_NIF, ENT_TELEFONE_FAX, ENT_CAIXA_POSTAL FROM GER_TERCEIROS T
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = TER_CODIGO_ENTIDADE
LEFT JOIN FIN_BANCO ON BANC_CODIGO = ENT_CODIGO
LEFT JOIN GER_MUNICIPIO ON MUN_CODIGO = ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA ON PROV_CODIGO = MUN_CODIGO_PROVINCIA  
WHERE TER_CODIGO_ENTIDADE = CODIGO;

END
;;
