CREATE PROCEDURE `stp_GER_VIATURA_ADICIONAR`(IN `CODIGO` int,IN `DESIGNACAO` varchar(120), IN `MATRICULA_ID` varchar(20), IN `OWNER_ID` int, IN `CHASSI` varchar(50),IN `MOTOR` varchar(50),IN `MARCA_ID` int,IN `FLUEL_ID` int,IN `CATEGORY_ID` int,IN `CILINDRADA` decimal(18,2),IN `COLOR_ID` int,IN `IMAGE_PATH` varchar(120),IN `SITUACAO` int,IN `CAIXA` varchar(1),IN `UTILIZADOR` varchar(20), CILINDROS INT, PNEUMATICOS VARCHAR(12), PESO DECIMAL(18,2),  TARA DECIMAL(18,2), EIXOS DECIMAL(18,2), PORTAS INT, ANO INT, CLASSE VARCHAR(1), VALVULAS INT, OUT LSTID INT)
BEGIN
	#Routine body goes here...
 
IF(COLOR_ID IS NULL OR COLOR_ID<=0) THEN
SET COLOR_ID = NULL;
END IF;

IF(EXISTS(SELECT * FROM GER_VIATURA WHERE VIA_LICENSE_ID=MATRICULA_ID)) AND (CODIGO IS NULL OR CODIGO <=0) THEN
SELECT VIA_CODIGO INTO CODIGO FROM GER_VIATURA WHERE VIA_LICENSE_ID=MATRICULA_ID;
END IF; 

IF(CODIGO IS NULL OR CODIGO <=0) THEN
INSERT INTO GER_VIATURA (VIA_DESIGNATION, VIA_CHASSI_NUMBER, VIA_MOTOR_NUMBER, VIA_MARCA_ID,
VIA_COMBUSTIVEL, VIA_CATEGORY_ID, VIA_CILINDRADA, VIA_COR_ID, VIA_PATH_IMAGE, VIA_STATUS, VIA_CAIXA, VIA_CREATED_BY, VIA_CREATED_DATE,
VIA_UPDATED_BY, VIA_UPDATED_DATE, VIA_CILINDROS, VIA_PNEUMATICOS, VIA_PESO, VIA_TARA, VIA_EIXOS, VIA_PORTAS, VIA_ANO, VIA_CLASSE, 
VIA_OWNER_ID, VIA_LICENSE_ID, VIA_VALVULAS) 
VALUES(DESIGNACAO, CHASSI, MOTOR, MARCA_ID, FLUEL_ID, CATEGORY_ID, CILINDRADA,
COLOR_ID, IMAGE_PATH, SITUACAO, CAIXA, UTILIZADOR, NOW(), UTILIZADOR, NOW(), CILINDROS, PNEUMATICOS, PESO, TARA, EIXOS, PORTAS, ANO, 
CLASSE, OWNER_ID, MATRICULA_ID,VALVULAS);
SELECT LAST_INSERT_ID() INTO CODIGO;
ELSE 
UPDATE GER_VIATURA SET 
VIA_DESIGNATION = DESIGNACAO,
VIA_CHASSI_NUMBER = CHASSI,
VIA_MOTOR_NUMBER = MOTOR,
VIA_MARCA_ID = MARCA_ID,
VIA_COMBUSTIVEL = FLUEL_ID,
VIA_CATEGORY_ID = CATEGORY_ID,
VIA_CILINDRADA = CILINDRADA,
VIA_COR_ID = COLOR_ID,
VIA_PATH_IMAGE = IMAGE_PATH,
VIA_STATUS = SITUACAO,
VIA_CAIXA = CAIXA,
VIA_UPDATED_BY = UTILIZADOR,
VIA_UPDATED_DATE = NOW(),
VIA_CILINDROS = CILINDROS,
VIA_PNEUMATICOS = PNEUMATICOS,
VIA_PESO = PESO,
VIA_TARA = TARA,
VIA_EIXOS = EIXOS,
VIA_PORTAS = PORTAS,
VIA_ANO = ANO,
VIA_CLASSE = CLASSE,
VIA_OWNER_ID = OWNER_ID,
VIA_LICENSE_ID = MATRICULA_ID
WHERE VIA_CODIGO = CODIGO;

END IF;
SELECT CODIGO INTO LSTID;

END
