delimiter ;;
CREATE PROCEDURE `stp_GER_VIATURA_OBTERPORFILTRO`(IN `DESIGNACAO` varchar(120),IN `MARCA_ID` int,IN `MODELO_ID` int, MATRICULA_ID VARCHAR(20), OWNER_ID int)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
 
SET v_CON_ = CONCAT("
SELECT VIA_CODIGO, VIA_DESIGNATION, VIA_LICENSE_ID, ENT_NOME_COMPLETO, VIA_LAST_INTERVENTION, VIA_PATH_IMAGE, NEXT_INTERVENTION  FROM GER_VIATURA  
LEFT JOIN GER_MARCA MA ON MA.MAR_CODIGO = VIA_MARCA_ID
LEFT JOIN GER_MARCA MO ON MO.MAR_CODIGO = VIA_MARCA_ID
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = VIA_OWNER_ID
WHERE VIA_DESIGNATION LIKE '%",DESIGNACAO,"%' AND VIA_LICENSE_ID LIKE '%",MATRICULA_ID,"%'");
 
IF(OWNER_ID > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND VIA_OWNER_ID = ", OWNER_ID); 
END IF;
IF(MARCA_ID > 0) THEN 
SET v_CON_=CONCAT(v_CON_," AND VIA_MARCA_ID = ", MARCA_ID); 
ELSE IF(MODELO_ID > 0) THEN 
SET v_CON_=CONCAT(v_CON_," AND VIA_MARCA_ID = ", MODELO_ID); 
END IF;
END IF;

SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
