delimiter ;;
CREATE PROCEDURE `stp_LAV_ATENDIMENTO_ADICIONAR`(IN `CODIGO` int,IN `ATENDENTE` varchar(11),IN `CLIENTE` varchar(11),IN `INICIO` datetime,IN `TERMINO` datetime,IN `FILIAL` varchar(11),IN `DESTINO` int, SITUACAO VARCHAR(1), UTILIZADOR VARCHAR(20), FATURA INT, OUT LSTID INT)
BEGIN


IF(CODIGO <= 0) THEN
 
		IF(FATURA > 0) THEN
			SET TERMINO = NOW();

		ELSE
			SET FATURA = NULL;
			SET TERMINO = NULL;
		 
		END IF;

IF(ATENDENTE ="-1") THEN
 SET ATENDENTE = NULL;
END IF;

		INSERT INTO rest_atendimento
		VALUES(DEFAULT, CLIENTE, CURDATE(), INICIO, TERMINO, 0, ATENDENTE,  DESTINO, SITUACAO, FILIAL, FATURA, UTILIZADOR, NULL, NULL);
		#SELECT LAST_INSERT_ID() INTO LSTID;

		IF(SITUACAO ="A") THEN
			UPDATE REST_ATENDIMENTO SET
			ATEND_TERMINO = NULL
			WHERE ATEND_CODIGO = CODIGO;
		END IF;		

SELECT LAST_INSERT_ID() INTO LSTID;
ELSE 
SELECT 0 INTO LSTID;
END IF;
END
;;
