delimiter ;;
CREATE PROCEDURE `stp_LAV_ATENDIMENTO_EXCLUIR`(IN `CODIGO` int, UTILIZADOR VARCHAR(20))
BEGIN
DECLARE MESA INT DEFAULT 0;
UPDATE REST_ATENDIMENTO
SET ATEND_STATUS = 'C',
ATEND_TERMINO = NOW(),
ATEND_DATA_FECHO = NOW(),
ATEND_FECHADO_POR = UTILIZADOR
WHERE ATEND_CODIGO = CODIGO;

SELECT IFNULL(ATEND_MESA_CODIGO_MESA, 0) INTO MESA FROM REST_ATENDIMENTO_SALA WHERE ATEND_MESA_ATENDIMENTO = CODIGO; 
IF(MESA >0) THEN
UPDATE REST_MESA 
SET MES_STATUS = 1
WHERE MES_CODIGO = MESA;
END IF;


END
;;
