delimiter ;;
CREATE PROCEDURE `stp_LAV_ATENDIMENTO_FECHAR`(IN `CODIGO` int,IN `ATENDENTE` varchar(11),IN `CLIENTE` varchar(11),  IN `TERMINO` datetime,IN `FILIAL` varchar(11),IN `DESTINO` int, SITUACAO VARCHAR(1), UTILIZADOR VARCHAR(20), FATURA INT)
BEGIN
	#Routine body goes here...
IF(CODIGO > 0) THEN

IF(FATURA > 0) THEN
		SET TERMINO = NOW();
		SET SITUACAO ='F';

	ELSE
		SET FATURA = NULL;
		SET TERMINO = NULL;
	 
END IF;

IF(ATENDENTE ="-1") THEN
 SET ATENDENTE = NULL;
END IF;

	UPDATE rest_atendimento SET
	ATEND_ATENDENTE = ATENDENTE,
	ATEND_CLIENTE = CLIENTE,
  ATEND_STATUS = SITUACAO,
  ATEND_CODIGO_FATURA = FATURA,
  ATEND_TERMINO = TERMINO,
  ATEND_STATUS = SITUACAO
	WHERE ATEND_CODIGO = CODIGO;  

	IF(TERMINO IS NOT NULL)THEN
		UPDATE REST_ATENDIMENTO 
		SET ATEND_FECHADO_POR = UTILIZADOR,
		ATEND_DATA_FECHO = NOW(),
		ATEND_STATUS ='F'
		WHERE ATEND_CODIGO = CODIGO;
  END IF;

END IF; 
END
;;
