	#Routine body goes here...
/*
DELETE FROM REST_ATENDIMENTO_ARTIGOS WHERE 
ATEND_ITEM_CODIGO_ATENDIMENTO = ATENDIMENTO 
AND ATEND_ITEM_CODIGO_ARTIGO = ARTIGO;*/
