BEGIN
	
SELECT A.ATEND_CODIGO, F.ENT_NOME_COMPLETO AS FUNCIONARIO, C.ENT_NOME_COMPLETO AS CLIENTE, A.ATEND_INICIO, A.ATEND_TERMINO, 
A.ATEND_CODIGO_FILIAL, A.ATEND_DESTINO, A.ATEND_STATUS, a.ATEND_ATENDENTE FROM rest_atendimento A
INNER JOIN REST_FUNCIONARIOS ON GAR_CODIGO_ENTIDADE = A.ATEND_ATENDENTE
INNER JOIN GER_ENTIDADE F ON F.ENT_CODIGO = GAR_CODIGO_ENTIDADE
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = A.ATEND_CLIENTE
INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = TER_CODIGO_ENTIDADE
WHERE ATEND_CODIGO = CODIGO;

END
