-- Procedure structure for stp_REST_ATENDIMENTO_CONSULTA_CONTA_ADICIONAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_REST_ATENDIMENTO_CONSULTA_CONTA_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_REST_ATENDIMENTO_CONSULTA_CONTA_ADICIONAR`(IN `ATENDIMENTO_ID` int,IN `UTILIZADOR` varchar(20))
BEGIN
	#Routine body goes here...
DECLARE DOCUMENT_ID INT DEFAULT 2;
DECLARE SERIE_ID INT;
DECLARE REFERENCIA VARCHAR(20); 
DECLARE DOCUMENT_NUMBER INT;
DECLARE ANO INT; 
DECLARE PREFIXO VARCHAR(12);

SELECT DOC_SIGLA INTO REFERENCIA FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENT_ID;
SELECT SER_CODIGO, SER_NUMERACAO, SER_ANO INTO SERIE_ID, DOCUMENT_NUMBER, ANO FROM COM_SERIE_DOCUMENTACAO 
WHERE SER_CODIGO_DOCUMENTO = DOCUMENT_ID AND SER_IS_ACTIVE = 1 AND SER_TERMINO > NOW();
SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCIA = CONCAT(REFERENCIA,"/",ANO, "-",PREFIXO);

INSERT INTO REST_ATENDIMENTO_CONSULTA_CONTA VALUES(DEFAULT,
DOCUMENT_ID,
SERIE_ID,
REFERENCIA,
DOCUMENT_NUMBER,
ATENDIMENTO_ID,
UTILIZADOR,
NOW(),
UTILIZADOR,
NOW());
 
SELECT LAST_INSERT_ID() INTO DOCUMENT_ID;
UPDATE COM_SERIE_DOCUMENTACAO SET SER_NUMERACAO = DOCUMENT_NUMBER WHERE SER_CODIGO = SERIE_ID;
SELECT DOC_DESCRICAO, CONS_REFERENCE, UTI_NOME FROM REST_ATENDIMENTO_CONSULTA_CONTA 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = CONS_DOCUMENT_ID
INNER JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = CREATED_BY
WHERE CONS_CODIGO = DOCUMENT_ID;

END
;;
