 SET SOLICITANTE = NULL;
END IF;



INSERT INTO REST_ATENDIMENTO_ARTIGOS(
ATEND_ITEM_CODIGO_ATENDIMENTO,
ATEND_ITEM_CODIGO_ARTIGO,
