DROP PROCEDURE IF EXISTS `stp_REST_ATENDIMENTO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_REST_ATENDIMENTO_OBTERPORFILTRO`(IN `ATENDIMENTO_ID` INT,IN `CREATION_INI` date,IN `CREATION_TERM` date,IN `CUSTOMER_ID` int,IN `EMPLOYEE_ID` int,IN `SITUACAO` varchar(2),IN `FILIAL` int, IN `CLOSED_INI` date,IN `CLOSED_TERM` date, IN `CANCELED_INI` date,IN `CANCELED_TERM` date, INVOICE VARCHAR(20), CREATED_BY VARCHAR(20), CLOSED_BY VARCHAR(20), CANCELED_BY VARCHAR(20))
BEGIN
	#Routine body goes here...

DECLARE v_CON_ LONGTEXT;
 
SET v_CON_ = CONCAT(" SELECT ATEND_CODIGO, ATEND_CONTA, E.ENT_NOME_COMPLETO AS FUNCIONARIO, ATEND_INICIO, C.ENT_NOME_COMPLETO AS CLIENTE,
ATEND_MESA_ID, MES_DESCRICAO, ATEND_TERMINO, ATEND_STATUS, UPPER(CR.uti_nome) AS CREATED_BY, UPPER(FX.uti_nome) AS CLOSED_BY, 
UPPER(AN.uti_nome) AS DELETED_BY, FAT_REFERENCIA   FROM REST_ATENDIMENTO 
INNER JOIN REST_MESA ON MES_CODIGO = ATEND_MESA_ID
INNER JOIN REST_FUNCIONARIOS ON GAR_CODIGO_ENTIDADE = ATEND_ATENDENTE
INNER JOIN GER_ENTIDADE E ON E.ENT_CODIGO = GAR_CODIGO_ENTIDADE
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ATEND_CLIENTE
INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = TER_CODIGO_ENTIDADE
INNER JOIN SIS_UTILIZADOR CR ON CR.UTI_UTILIZADOR = ATEND_CRIADO_POR
LEFT JOIN SIS_UTILIZADOR FX ON FX.uti_utilizador = ATEND_FECHADO_POR
LEFT JOIN SIS_UTILIZADOR AN ON AN.uti_utilizador = ATEND_ANULADO_POR
LEFT JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = ATEND_CODIGO_FATURA
WHERE ATEND_CONTA > 0 AND ATEND_CODIGO_FILIAL = ",FILIAL );

IF(ATENDIMENTO_ID > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND ATEND_CODIGO = ", ATENDIMENTO_ID); 
END IF;

IF(CUSTOMER_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND ATEND_CLIENTE = ", CUSTOMER_ID); 
END IF;

IF(EMPLOYEE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND ATEND_ATENDENTE = ", EMPLOYEE_ID); 
END IF;

IF(SITUACAO != "-1") THEN
 SET v_CON_=CONCAT(v_CON_," AND ATEND_STATUS = '", SITUACAO, "'"); 
END IF; 

IF(CREATED_BY != "-1") THEN
 SET v_CON_=CONCAT(v_CON_," AND ATEND_CRIADO_POR = '", CREATED_BY, "'"); 
END IF; 

IF(CLOSED_BY != "-1") THEN
 SET v_CON_=CONCAT(v_CON_," AND ATEND_FECHADO_POR = '", CLOSED_BY, "'"); 
END IF; 

IF(CANCELED_BY != "-1") THEN
 SET v_CON_=CONCAT(v_CON_," AND ATEND_ANULADO_POR = '", CANCELED_BY, "'"); 
END IF; 
 IF(INVOICE IS NOT NULL AND INVOICE !="") THEN
 SET v_CON_= CONCAT(v_CON_, " OR FAT_REFERENCIA LIKE '%",INVOICE,"%'");
END IF;
IF(CREATION_INI IS NOT NULL AND CREATION_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_INICIO BETWEEN '",CREATION_INI, "' AND '", CREATION_TERM, "'");
ELSE IF(CREATION_INI IS NOT NULL AND CREATION_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_INICIO >= '",CREATION_INI, "'");
ELSE IF(CREATION_INI IS NULL AND CREATION_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_INICIO <= '", CREATION_TERM, "'");
END IF;
END IF;
END IF;

IF( CLOSED_INI IS NOT NULL AND  CLOSED_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_DATA_FECHO BETWEEN '", CLOSED_INI, "' AND '",  CLOSED_TERM, "'");
ELSE IF( CLOSED_INI IS NOT NULL AND  CLOSED_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_DATA_FECHO >= '", CLOSED_INI, "'");
ELSE IF( CLOSED_INI IS NULL AND  CLOSED_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_DATA_FECHO <= '",  CLOSED_TERM, "'");
END IF;
END IF;
END IF;



IF(CANCELED_INI IS NOT NULL AND  CANCELED_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_CANCEL_DATE BETWEEN '", CANCELED_INI, "' AND '",  CANCELED_TERM, "'");
ELSE IF( CANCELED_INI IS NOT NULL AND  CANCELED_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_CANCEL_DATE >= '", CANCELED_INI, "'");
ELSE IF( CANCELED_INI IS NULL AND  CANCELED_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND ATEND_CANCEL_DATE <= '",  CANCELED_TERM, "'");
END IF;
END IF;
END IF;


SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;


END
;;
delimiter ;

