delimiter ;;
CREATE PROCEDURE `stp_REST_PEDIDO_COZINHA_OBTERPORFILTRO`(pFROM DATE, pUNTIL DATE, ATENDENTE INT, FILIAL INT)
BEGIN
	#Routine body goes here...
DECLARE v_CONSULTA LONGTEXT;

SET v_CONSULTA = CONCAT("
SELECT PED_CODIGO, PED_NUMERO_DIA, PED_DATA, ART_DESIGNACAO, IFNULL(MES_DESCRICAO, '') AS MESA FROM REST_PEDIDO_COZINHA P
INNER JOIN GER_ARTIGO ON ART_CODIGO = PED_CODIGO_ARTIGO
INNER JOIN REST_ATENDIMENTO ON ATEND_CODIGO = PED_CODIGO_ATENDIMENTO
LEFT JOIN REST_ATENDIMENTO_SALA ON ATEND_MESA_ATENDIMENTO = ATEND_CODIGO
LEFT JOIN REST_MESA ON MES_CODIGO = ATEND_MESA_CODIGO_MESA
WHERE PED_FILIAL =", FILIAL);
IF(pFROM IS NOT NULL  AND pUntil IS NOT NULL) THEN
SET v_CONSULTA =CONCAT(v_CONSULTA," AND  FAT_DATA_FATURA BETWEEN  '",pFROM, "' AND '",pUNTIL, "'");
ELSE IF(pFROM IS NOT NULL AND pUntil IS NULL )
THEN
SET v_CONSULTA =CONCAT(v_CONSULTA," AND FAT_DATA_FATURA >='",pFROM, "'");
ELSE IF(pFROM IS NULL  AND pUntil IS NOT NULL)
THEN
SET v_CONSULTA =CONCAT(v_CONSULTA," AND FAT_DATA_FATURA <= '",pUNTIL, "'");
END IF;
 END IF;
END IF; 

END
;;
