-- Procedure structure for stp_SAFT_MOVEMENTOFGOODS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_SAFT_MOVEMENTOFGOODS`;
delimiter ;;
CREATE PROCEDURE `stp_SAFT_MOVEMENTOFGOODS`(IN `COMPANY_ID` int,IN `DATE_INI` date,IN `DATE_TERM` date,IN `TRANSACTION_ID` int, IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here...
DECLARE MOVEMENTLINES INT DEFAULT 0;
DECLARE TOTALISSUED DECIMAL(18,2) DEFAULT 0;

SELECT COUNT(FAT_CODIGO) , SUM(FAT_VALOR_TOTAL - (FAT_VALOR_PAGO - FAT_TROCO)) INTO MOVEMENTLINES FROM COM_FATURA_CLIENTE  
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
WHERE FAT_DATA_EMISSAO BETWEEN EMISSAO_INI AND EMISSAO_TERM AND FAT_AGT_SENT =0 AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR AND FAT_STATUS_DOCUMENTO =8 AND DOC_FORMATO IN('TRANSP_V');

SELECT SUM(FAT_ITEM_QUANTIDADE) INTO TOTALISSUED FROM COM_FATURA_CLIENTE  
