	FAT_LOCAL_DESCARGA AS ShipToAddressDetail, '' AS ShipToCity, '' AS ShipToPostalCode, '' AS ShipToProvince,
	'AO' as ShipToCountry, 
FAT_LOCAL_CARGA as ShipFromDeliveryID, FAT_DATA_ENTREGA as ShipFromDeliveryDate, '' as ShipFromWarehouseID, '' as ShipFromLocationID,
'' AS ShipFromBuildingNumber, '' AS ShipFromStreetName, FAT_LOCAL_CARGA AS ShipFromAddressDetail, '' AS ShipFromCity, '' AS ShipFromPostalCode, '' AS ShipFromProvince,
	'AO' as ShipFromCountry, FAT_DATA_ENTREGA as MovementEndTime, FAT_DATA_ENTREGA as MovementStartTime, FAT_CODIGO, 
'f70000028201804' as AGTDocCodeID, FAT_TOTAL_IMPOSTOS AS TaxPayable, (FAT_VALOR_BRUTO - FAT_TOTAL_DESCONTOS) AS NetTotal, FAT_VALOR_TOTAL AS GrossTotal, 
 MOE_SIGLA AS CurrencyCode, (FAT_VALOR_TOTAL/FAT_CAMBIO) AS CurrencyAmount, FAT_CAMBIO AS ExchangeRate    FROM COM_FATURA_CLIENTE
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
INNER JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA
