BEGIN
	#Routine body goes here...
DECLARE NumberOfEntries INT DEFAULT 0;
DECLARE TotalDebit DECIMAL(18, 2) DEFAULT 0;
DECLARE TotalCredit DECIMAL(18, 2) DEFAULT 0;



SELECT COUNT(REC_CODIGO) INTO NumberOfEntries FROM COM_RECIBO_CLIENTE 
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO 
WHERE REC_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND 
SER_ANO = FISCAL_YEAR AND SER_CODIGO_FILIAL = COMPANY_ID; 


SELECT SUM(REC_VALOR_TOTAL) INTO TotalCredit FROM COM_RECIBO_CLIENTE 
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO 
WHERE REC_STATUS =1 AND REC_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND 
SER_ANO = FISCAL_YEAR AND SER_CODIGO_FILIAL = COMPANY_ID
AND DOC_FORMATO !="RECEIPT_R"; 

SELECT SUM(REC_VALOR_TOTAL) INTO TotalCredit FROM COM_RECIBO_CLIENTE 
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO 
WHERE REC_STATUS =1 AND REC_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND 
SER_ANO = FISCAL_YEAR AND SER_CODIGO_FILIAL = COMPANY_ID
AND DOC_FORMATO ="RECEIPT_R"; 




SELECT NumberOfEntries, TotalDebit, TotalCredit, CONCAT(DOC_SIGLA," ",SER_CODIGO, "/", REC_NUMERACAO) AS PaymentRefNo, MONTH(REC_CREATED_DATE) AS Period, 'N/A' as TransationID,  REC_DATA_EMISSAO AS TransactionDate, DOC_SIGLA AS PaymentType, '' as Description, REC_CODIGO AS SystemID, CASE WHEN REC_STATUS = 0  THEN 'A' ELSE 'N' END AS PaymentStatus, 
REC_UPDATED_DATE AS PaymentStatusDate, REC_MOTIVO_ANULACAO as Reason, REC_UPDATED_BY as PaymentStatusSourceID, 'P' as SourcePayment, 0 as TaxPayable, 
REC_VALOR_PAGO as NetTotal, 0 as GrossTotal, format((REC_VALOR_PAGO * (REC_DESCONTO/100)), 2) as SettlementAmount, MOE_SIGLA as CurrencyCode,
0 as CurrencyAmount, format(REC_CAMBIO,2) AS ExchangeRate, REC_CODIGO as PaymentID, REC_CREATED_BY AS SourceID, REC_CREATED_DATE AS SystemEntryDate, REC_CODIGO_ENTIDADE as CustomerID
FROM COM_RECIBO_CLIENTE  
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO 
INNER JOIN FIN_MOEDA ON MOE_CODIGO = REC_CODIGO_MOEDA
WHERE REC_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND 
SER_ANO = FISCAL_YEAR AND SER_CODIGO_FILIAL = COMPANY_ID; 

END
;;
