CREATE PROCEDURE `stp_SAFT_SALES_INVOICES`(IN `COMPANY_ID` int, FILE_TYPE varchar(1), IN `DATE_INI` date,IN `DATE_TERM` date,IN `TRANSACTION_ID` int, IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here... 


SELECT CONCAT(DOC_SIGLA," ",SER_CODIGO, "/", FAT_NUMERACAO) AS InvoiceNumber, 
CASE
			WHEN FAT_STATUS = 0  THEN 'A'
			ELSE 'N'
	END AS InvoiceStatus, FAT_UPDATED_DATE AS InvoiceStatusDate, 
'' AS Reason, FAT_UPDATE_BY as StatusSourceID, 'P' as SourceBilling, FAT_HASH as Hash, 1 as HasControl, 
MONTH(FAT_DATA_EMISSAO) AS Period, FAT_DATA_EMISSAO as InvoiceDate, DOC_SIGLA AS InvoiceType,
0 as SelfBillingIndicator, 0 as CashVATSchemeIndicator, 0 as ThirdPartiesBillingIndicator, FAT_CREATED_BY as SourceID, 
'' as EACCode, FAT_CREATED_DATE AS SystemEntryDate, /*"Desconhecido"*/ NULL as TransactionID, TER_CODIGO AS CustomerID, 
FAT_LOCAL_DESCARGA as ShipToDeliveryID, FAT_DATA_ENTREGA as ShipToDeliveryDate, '' as ShipToWarehouseID, '' as ShipToLocationID,
'' AS ShipToBuildingNumber, '' AS ShipToStreetName, 
	IFNULL(FAT_MORADA_DESCARGA, 'N/A') AS ShipToAddressDetail, IFNULL(FAT_LOCAL_DESCARGA, 'N/A') AS ShipToCity, '' AS ShipToPostalCode, '' AS ShipToProvince,
	'AO' as ShipToCountry, 
FAT_LOCAL_CARGA as ShipFromDeliveryID, FAT_DATA_ENTREGA as ShipFromDeliveryDate, '' as ShipFromWarehouseID, '' as ShipFromLocationID,
'' AS ShipFromBuildingNumber, '' AS ShipFromStreetName, IFNULL(FAT_MORADA_CARGA, 'N/A') AS ShipFromAddressDetail, MUN_DESCRICAO AS ShipFromCity, 
'' AS ShipFromPostalCode, '' AS ShipShipFromProvince,
	'AO' as ShipFromCountry, IFNULL(IFNULL(FAT_DATA_CARGA, FAT_DATA_ENTREGA), FAT_CREATED_DATE) as MovementEndTime, IFNULL(IFNULL(FAT_DATA_CARGA, FAT_DATA_ENTREGA), FAT_CREATED_DATE) as MovementStartTime, FAT_CODIGO, 
 FAT_TOTAL_IMPOSTOS AS TaxPayable, (FAT_VALOR_BRUTO - FAT_TOTAL_DESCONTOS) AS NetTotal, FAT_VALOR_TOTAL AS GrossTotal, 
 MOE_SIGLA AS CurrencyCode, (FAT_VALOR_TOTAL/FAT_CAMBIO) AS CurrencyAmount, FAT_CAMBIO AS ExchangeRate, '' AS SettlementDiscount, 
 FAT_TOTAL_DESCONTOS AS SettlementAmount, '' AS SettlementDate, '' AS PaymentTerms, BuildingNumber, StreetName, AddressDetail,
City, PostalCode, Province, IFNULL(Country, "AO") as Country, IFNULL(FAT_VALOR_RETENCAO, 0) AS WithholdingTaxAmount,
IFNULL(SIS_RETENTION_TAX, 0) AS WithholdingTaxType
 

FROM COM_FATURA_CLIENTE
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = FAT_CODIGO_CLIENTE
INNER JOIN SIS_EMPRESA ON FIL_CODIGO = FAT_CODIGO_FILIAL
INNER JOIN ger_entidade on ENT_CODIGO = FIL_CODIGO
INNER JOIN ger_municipio on MUN_CODIGO = ENT_LOCAL_MORADA
INNER JOIN ger_provincia ON PROV_CODIGO = MUN_CODIGO_PROVINCIA
INNER JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA
LEFT JOIN COM_FATURA_CLIENTE_MORADA ON InvoiceId = FAT_CODIGO
LEFT JOIN SIS_CONFIGURACAO ON SIS_CODIGO_FILIAL = FAT_CODIGO_FILIAL
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR /*AND FAT_STATUS_DOCUMENTO =8*/ AND DOC_FORMATO IN('INVOICE', 'INVOICE_R', 'INVOICE_P', 'INVOICE_D')
AND FAT_NUMERACAO > 0 ORDER BY FAT_CODIGO_SERIE, FAT_NUMERACAO;


END
;;
