DROP PROCEDURE IF EXISTS `stp_SAFT_SALES_INVOICES_LINES`;
delimiter ;;
CREATE PROCEDURE `stp_SAFT_SALES_INVOICES_LINES`(INVOICE_ID INT)
BEGIN
	#Routine body goes here...
SELECT FAT_ORDEM AS LineNumber, DOC_ORI.FAT_REFERENCIA AS OriginatingON, DOC_ORI.FAT_DATA_EMISSAO AS OrderDate, FAT_ITEM_CODIGO_ARTIGO AS ProductCode,
ART_DESIGNACAO AS ProductDescription, FAT_ITEM_QUANTIDADE AS Quantity, (FAT_ITEM_PRECO - FAT_ITEM_VALOR_DESCONTOS) AS UnitPrice, UNI_SIGLA AS UnitOfMeasure, 
((FAT_ITEM_PRECO - FAT_ITEM_VALOR_DESCONTOS) * FAT_ITEM_QUANTIDADE) AS TaxBase, IFNULL(F.FAT_DATA_ENTREGA, F.FAT_DATA_EMISSAO) AS TaxPointDate, '' AS Reference, '' AS Resason, 
IFNULL(ART_DESCRICAO_COMERCIAL, ART_DESIGNACAO) AS Description, SERIAL_SERIAL_NUMBER SerialNumber, ((FAT_ITEM_PRECO * FAT_ITEM_QUANTIDADE)-(FAT_ITEM_VALOR_DESCONTOS + FAT_ITEM_VALOR_DESCONTO_FINANCEIRO)) AS DebitAmount, 0 AS CreditAmount,
IFNULL(IMP_TIPO, 'NS') AS TaxType, 'AO' as TaxCountryRegion, IFNULL(IMP_CATEGORIA, 'NS') AS TaxCode, IFNULL(IMP_VALOR,0) AS TaxPercentage, 
CASE
			WHEN IMP_VALORIZACAO = 'N'  THEN IMP_VALOR 
			ELSE 0
	END AS TaxAmount,
CASE 
WHEN IMP_VALOR = 0 THEN   
IMP_DESCRICAO ELSE NULL END
AS TaxExemptionReason, CASE WHEN IMP_VALOR = 0 THEN IFNULL(IMP_SIGLA, 'N/A') ELSE NULL END AS TaxExemptionCode, FAT_ITEM_VALOR_DESCONTOS AS SettlementAmount, '' AS ARCNo,
0 AS IECAmount 
FROM COM_FATURA_CLIENTE_ITEM 
INNER JOIN COM_FATURA_CLIENTE F ON F.FAT_CODIGO = FAT_ITEM_CODIGO_FATURA 
LEFT JOIN COM_FATURA_CLIENTE DOC_ORI ON DOC_ORI.FAT_CODIGO = FAT_ITEM_DOC_ORIGEM_ID
INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
LEFT JOIN GER_UNIDADE_MEDICAO M ON M.UNI_CODIGO = ART_UNIDADE_VENDA
LEFT JOIN GER_ARTIGO_SERIAL_NUMBER ON SERIAL_ARTIGO_ID = FAT_ITEM_SERIALNUMBER_ID
LEFT JOIN GER_IMPOSTOS ON IMP_CODIGO = FAT_ITEM_IMPOSTO_ID
WHERE FAT_ITEM_CODIGO_FATURA = INVOICE_ID AND F.FAT_NUMERACAO > 0;

END
;;
