delimiter ;;
CREATE PROCEDURE `stp_SAFT_SALES_INVOICES_PAYMENTS`(INVOICE_ID INT)
BEGIN
	#Routine body goes here...
SELECT FAT_CODIGO, PAG_SHORTNAME AS PaymentMechanism, MOV_VALOR AS PaymentAmount, MOV_DATA AS PaymentDate FROM `ger_meio_pagamento`
INNER JOIN fin_movimento_caixa ON MOV_MEIO_PAGAMENTO = PAG_CODIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_DOCUMENT_ID
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
