INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = SER_CODIGO_DOCUMENTO
INNER JOIN FIN_MOVIMENTO_CAIXA ON MOV_DOCUMENT_TYPE = R.DOC_CODIGO
INNER JOIN GER_MEIO_PAGAMENTO ON PAG_CODIGO = MOV_MEIO_PAGAMENTO 
INNER JOIN COM_RECIBO_CLIENTE_DOCUMENTOS ON REC_DOC_RECIBO_ID = REC_CODIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = REC_DOC_DOCUMENTO_ID
INNER JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE F.DOC_FORMATO='INVOICE' AND FAT_CODIGO = INVOICE_ID;

END
