-- ----------------------------
-- Procedure structure for stp_SAFT_SALES_INVOICES_TOTAL_ENTRIES_DEBITS_CREDITS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_SAFT_SALES_INVOICES_TOTAL_ENTRIES_DEBITS_CREDITS`;
delimiter ;;
CREATE PROCEDURE `stp_SAFT_SALES_INVOICES_TOTAL_ENTRIES_DEBITS_CREDITS`(IN `COMPANY_ID` int,IN `DATE_INI` date,IN `DATE_TERM` date,IN `TRANSACTION_ID` int, IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here... 
	DECLARE NumberOfEntries int(11) DEFAULT 0;
	DECLARE TotalDebit decimal(18,2) DEFAULT 0;
	DECLARE TotalCredit decimal(18,2) DEFAULT 0;
  DECLARE TotalIva decimal(18,2) DEFAULT 0;	
	
	
	
SELECT COUNT(FAT_CODIGO) into NumberOfEntries FROM COM_FATURA_CLIENTE  
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO 
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR /*AND FAT_STATUS_DOCUMENTO = 8*/ AND DOC_FORMATO IN('INVOICE', 'INVOICE_R', 'INVOICE_P', 'INVOICE_D') AND FAT_NUMERACAO > 0 AND FAT_CODIGO IN (SELECT FAT_ITEM_CODIGO_FATURA FROM COM_FATURA_CLIENTE_ITEM);


SELECT IFNULL(SUM(FAT_ITEM_VALOR_IMPOSTOS),0) into TotalIva FROM COM_FATURA_CLIENTE_ITEM 
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA 
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO  
LEFT JOIN GER_IMPOSTOS ON IMP_CODIGO = FAT_ITEM_IMPOSTO_ID
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR AND FAT_STATUS_DOCUMENTO = 8 AND DOC_FORMATO IN('INVOICE_P', 'INVOICE_D')
AND IMP_TIPO = "IVA" AND FAT_NUMERACAO > 0;

SELECT (SUM(FAT_ITEM_PRECO * FAT_ITEM_QUANTIDADE) /*+ TotalIva*/) - SUM(FAT_ITEM_VALOR_DESCONTOS) into TotalDebit FROM COM_FATURA_CLIENTE_ITEM 
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA  
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO  
LEFT JOIN GER_IMPOSTOS ON IMP_CODIGO = FAT_ITEM_IMPOSTO_ID
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
AND SER_ANO = FISCAL_YEAR AND FAT_STATUS_DOCUMENTO = 8 AND DOC_FORMATO IN('INVOICE_P', 'INVOICE_D') AND FAT_STATUS = 1
AND FAT_NUMERACAO > 0;


SELECT IFNULL(SUM(FAT_ITEM_VALOR_IMPOSTOS),0) into TotalIva FROM COM_FATURA_CLIENTE_ITEM 
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA 
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO  
LEFT JOIN GER_IMPOSTOS ON IMP_CODIGO = FAT_ITEM_IMPOSTO_ID
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR AND FAT_STATUS_DOCUMENTO = 8 AND DOC_FORMATO IN('INVOICE', 'INVOICE_R')
AND IMP_TIPO = "IVA" AND FAT_NUMERACAO > 0;

SELECT (SUM(FAT_ITEM_PRECO * FAT_ITEM_QUANTIDADE) /*+ TotalIva*/) - SUM(FAT_ITEM_VALOR_DESCONTOS) into TotalCredit FROM COM_FATURA_CLIENTE_ITEM 
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA  
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO  
LEFT JOIN GER_IMPOSTOS ON IMP_CODIGO = FAT_ITEM_IMPOSTO_ID
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
AND SER_ANO = FISCAL_YEAR AND FAT_STATUS_DOCUMENTO = 8 AND DOC_FORMATO IN('INVOICE', 'INVOICE_R')  AND FAT_STATUS = 1
AND FAT_NUMERACAO > 0;




 
SELECT NumberOfEntries, IFNULL(TotalDebit, "0.00") as TotalDebit, IFNULL(TotalCredit,"0.00") as TotalCredit;

END
;;
