DROP PROCEDURE IF EXISTS `stp_SAFT_SUPPLIERS`;
delimiter ;;
CREATE PROCEDURE `stp_SAFT_SUPPLIERS`(IN `COMPANY_ID` int,IN `DATE_INI` date,IN `DATE_TERM` date,IN `TRANSACTION_ID` int, IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here...

INSERT INTO SUPPLIER
SELECT TER_CODIGO AS SupplierID, IFNULL(PLAN_CONTA, 'Desconhecido') as AccountID, ENT_IDENTIFICACAO AS SupplierTaxID, 
ENT_NOME_COMPLETO AS CompanyName, '' AS Contact, '' as BuildingNumber, ENT_RUA AS StreetName, 
	CONCAT(ENT_RUA, ' ', ENT_BAIRRO, ' ',  ENT_DISTRITO) AS AddressDetail, MUN_DESCRICAO AS City, '' AS PostalCode, PROV_DESCRICAO AS Province,
	'AO' as Country, ENT_TELEFONE AS Telephone,
	ENT_TELEFONE_FAX AS Fax, ENT_EMAIL AS Email, ENT_WEBSITE AS Website, IFNULL(TER_SELFBILLING, 0) AS SelfBillingIndicator  
FROM GER_TERCEIROS T 
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = TER_CODIGO_ENTIDADE
LEFT JOIN FIN_PLANO_CONTAS ON PLAN_CODIGO = TER_ACCOUNT_SUPPLIER_ID
inner join ger_municipio on MUN_CODIGO = ENT_LOCAL_MORADA
	INNER JOIN ger_provincia ON PROV_CODIGO = MUN_CODIGO_PROVINCIA
WHERE TER_EXPORTED = 0 AND TER_CODIGO_ENTIDADE IN (SELECT FAT_CODIGO_FORNECEDOR FROM COM_FATURA_FORNECEDOR
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR);
 
END
;;
