BEGIN
	#Routine body goes here...
select IFNULL(IMP_TIPO,'NS') as TaxType, IMP_ZONA_FISCAL AS TaxCountryRegion, IMP_CATEGORIA AS TaxCode, IMP_DESCRICAO AS Description, 
CONCAT(YEAR(CURDATE()),'-12-31') AS TaxExpirationDate, IMP_VALOR AS TaxPercentage, 
CASE
			WHEN IMP_VALORIZACAO = 'N'  THEN IMP_VALOR 
			ELSE 0
	END AS TaxAmount from ger_impostos
WHERE IMP_CODIGO IN(select FAT_ITEM_IMPOSTO_ID from com_fatura_cliente_item
inner join com_fatura_cliente on fat_codigo = FAT_ITEM_CODIGO_FATURA
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR);
END
;;
