-- ----------------------------
-- Procedure structure for stp_SAFT_WORKING_DOCUMENTS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_SAFT_WORKING_DOCUMENTS`;
delimiter ;;
CREATE PROCEDURE `stp_SAFT_WORKING_DOCUMENTS`(IN `COMPANY_ID` int,IN `DATE_INI` date,IN `DATE_TERM` date,IN `TRANSACTION_ID` int, IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here...

SELECT  
CONCAT(DOC_SIGLA," ",SER_CODIGO, "/", FAT_NUMERACAO) as DocumentNumber, CASE
			WHEN FAT_STATUS = 0  THEN 'A'
			ELSE 'N'
	END AS WorkStatus, FAT_UPDATED_DATE AS WorkStatusDate, 
'' AS Reason, FAT_UPDATE_BY as StatusSourceID, 'P' as SourceBilling, FAT_HASH as Hash, 1 as HasControl, 
MONTH(FAT_DATA_EMISSAO) AS Period, FAT_DATA_EMISSAO as WorkDate, DOC_SIGLA AS WorkType, FAT_CREATED_BY as SourceID,
'' as EACCode, FAT_CREATED_DATE AS SystemEntryDate, NULL as TransactionID, TER_CODIGO AS CustomerID, FAT_CODIGO as WorkID,
FAT_TOTAL_IMPOSTOS AS TaxPayable, (FAT_VALOR_BRUTO - FAT_TOTAL_DESCONTOS) AS NetTotal, FAT_VALOR_TOTAL AS GrossTotal, 
 MOE_SIGLA AS CurrencyCode, (FAT_VALOR_TOTAL/FAT_CAMBIO) AS CurrencyAmount, FAT_CAMBIO AS ExchangeRate  FROM COM_FATURA_CLIENTE
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = FAT_CODIGO_CLIENTE
INNER JOIN SIS_EMPRESA ON FIL_CODIGO = FAT_CODIGO_FILIAL
INNER JOIN ger_entidade on ENT_CODIGO = FIL_CODIGO
INNER JOIN ger_municipio on MUN_CODIGO = ENT_LOCAL_MORADA
INNER JOIN ger_provincia ON PROV_CODIGO = MUN_CODIGO_PROVINCIA
INNER JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA  
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR AND FAT_NUMERACAO > 0 AND DOC_FORMATO NOT IN('INVOICE', 'INVOICE_R', 'INVOICE_P', 'INVOICE_D', 'TRANSP_V');

END
;;
