-- ----------------------------
-- Procedure structure for stp_SAFT_WORKING_DOCUMENTS_TOTAL_ENTRIES_DEBITS_CREDITS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_SAFT_WORKING_DOCUMENTS_TOTAL_ENTRIES_DEBITS_CREDITS`;
delimiter ;;
CREATE PROCEDURE `stp_SAFT_WORKING_DOCUMENTS_TOTAL_ENTRIES_DEBITS_CREDITS`(IN `COMPANY_ID` int,IN `DATE_INI` date,IN `DATE_TERM` date,IN `TRANSACTION_ID` int, IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here... 
SELECT COUNT(FAT_CODIGO) as NumberOfEntries, SUM(FAT_VALOR_TOTAL - (FAT_VALOR_PAGO - FAT_TROCO)) as TotalDebit, 
SUM(FAT_VALOR_PAGO - FAT_TROCO)as TotalCredit FROM COM_FATURA_CLIENTE  
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_ANO_FATURACAO ON ANO_ANO = SER_ANO 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
WHERE FAT_DATA_EMISSAO BETWEEN DATE_INI AND DATE_TERM  AND FAT_CODIGO_FILIAL = COMPANY_ID 
and SER_ANO = FISCAL_YEAR AND FAT_NUMERACAO > 0 AND DOC_FORMATO 
NOT IN('INVOICE', 'INVOICE_R', 'INVOICE_P', 'INVOICE_D', 'TRANSP_V');

END
;;
