delimiter ;;
CREATE PROCEDURE `stp_SIS_CONFIGURACAO_ACTIVA`(IN `CODIGO` INT)
BEGIN
	#Routine body goes here...
SELECT C.*, P.*, F.FIL_NOME_COMERCIAL, F.FIL_RAZAO_SOCIAL, E.ENT_FOTOGRAFIA_PATH, 
IFNULL(F.FIL_CATEGORIA, -1) AS FIL_CATEGORIA, ENT_IDENTIFICACAO,
ENT_BAIRRO, ENT_RUA, ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, ENT_EMAIL, ENT_WEBSITE, 
PROV_DESCRICAO, MUN_DESCRICAO, PROV_CODIGO, MOE_SIGLA AS PAI_MOEDA, DOC_DESCRICAO, FIL_SUJEITO_PASSIVO
FROM SIS_CONFIGURACAO C
LEFT JOIN COM_POS_CONFIGURACAO P ON P.POS_CONFIG_CODIGO_FILIAL = C.SIS_CODIGO_FILIAL
INNER JOIN SIS_EMPRESA F ON F.FIL_CODIGO = C.SIS_CODIGO_FILIAL
INNER JOIN GER_ENTIDADE E ON ENT_CODIGO = FIL_CODIGO
INNER JOIN GER_MUNICIPIO ON MUN_CODIGO = ENT_LOCAL_MORADA
INNER JOIN GER_PROVINCIA ON PROV_CODIGO = MUN_CODIGO_PROVINCIA
INNER JOIN FIN_MOEDA ON MOE_CODIGO=C.SIS_MOEDA_PADRAO
LEFT JOIN COM_DOCUMENTO  ON  DOC_CODIGO = P.POS_CONFIG_DOCUMENT
WHERE SIS_CODIGO_FILIAL = CODIGO;
END
;;
