DROP PROCEDURE IF EXISTS `stp_SIS_EMPRESA_ACESSO`;
delimiter ;;
CREATE PROCEDURE `stp_SIS_EMPRESA_ACESSO`(IN `UTILIZADOR` varchar(20))
BEGIN
	#Routine body goes here...
SELECT S.FIL_CODIGO, FIL_NOME_COMERCIAL, ENT_RUA, ENT_BAIRRO, ENT_TELEFONE, ENT_EMAIL, S.FIL_CATEGORIA, IFNULL(UTI_FILIAL_DEFAULT,0) 
AS UTI_FILIAL_DEFAULT, ENT_IDENTIFICACAO, ENT_FOTOGRAFIA_PATH, FIL_SUJEITO_PASSIVO   
FROM SIS_UTILIZADOR_FILIAL UF
INNER JOIN SIS_EMPRESA S ON S.FIL_CODIGO = UF.UTI_FILIAL_CODIGO_FILIAL
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = UF.UTI_FILIAL_CODIGO_FILIAL
WHERE UF.UTI_FILIAL_UTILIZADOR = UTILIZADOR;
END
;;
