CREATE PROCEDURE `stp_SIS_EMPRESA_ADICIONAR`(IN `CODIGO` integer, IN`NOME_COMPLETO` VARCHAR(120), IN `DATA_NASCIMENTO` date,IN `LOCAL_NASCIMENTO` integer,IN `NACIONALIDADE` integer,IN `IDENTIFICACAO`varchar(18),IN `DOCUMENTO` integer, IN `LOCAL_MORADA` integer,IN `RUA` varchar(120),IN `BAIRRO` varchar(120),IN `TELEFONE` varchar(18),IN `TELF_ALT` varchar(18),IN `FAX` varchar(18),IN `EMAIL` varchar(40),IN `WEBSITE` varchar(40), IN `SITUACAO` bit,IN `INCLUSAO` date, IN `RAZAO` varchar(120),IN `FANTASIA` varchar(120),IN `EMPRESA` integer, IN `LOGOTIPO` longblob, IN `PATH` varchar(100), IN `EMISSAO`  date, IN `VALIDADE` DATE, IN `LOCAL_EMISSAO` VARCHAR(20), IN CATEGORIA INT, INICIAIS VARCHAR(5), ISPASSIVO VARCHAR(1), RETENCAO INT, OUT `LSTID` integer)
