 SELECT RET_VALOR INTO RETENTION_VALUE FROM GER_RETENCAO_FONTE WHERE RET_CODIGO = RETENCAO;
INSERT INTO SIS_CONFIGURACAO VALUES (CODIGO, 2, 1, 1, 'FIL', 2, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, RETENTION_VALUE, 1, "C:\\data");
INSERT INTO GER_ARMAZEM VALUES (DEFAULT, 'ARMAZÉM GERAL', CODIGO, (CODIGO+1), '1', '1', '0', '1', 'S', b'1', b'0', b'0', 'administrador', NOW(), 'administrador', NOW(), NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL); 
