 END IF;

 SELECT ENT_CODIGO_DOCUMENTO, ENT_IDENTIFICACAO INTO DOCUMENTO, IDENTIFICACAO FROM GER_ENTIDADE WHERE ENT_CODIGO = EMPRESA;
ELSE
  INSERT INTO GER_ENTIDADE_DOCUMENTACAO (ENT_DOC_CODIGO_ENTIDADE, ENT_DOC_CODIGO_DOCUMENTO, ENT_DOC_NUMERO,
  ENT_DOC_EMISSAO, ENT_DOC_VALIDADE, ENT_DOC_LOCAL_EMISSAO) 
  VALUES(CODIGO, DOCUMENTO, IDENTIFICACAO, EMISSAO, VALIDADE, LOCAL_EMISSAO);
  SELECT LAST_INSERT_ID() INTO DOCUMENTO;
