DROP PROCEDURE IF EXISTS `stp_SIS_EMPRESA_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_SIS_EMPRESA_OBTERPORFILTRO`(IN`EMPRESA`integer)
BEGIN
	#Routine body goes here...
IF(EMPRESA >0) THEN

SELECT E.ENT_CODIGO,  FIL_NOME_COMERCIAL, 
CONCAT(E.ENT_BAIRRO,' ',E.ENT_RUA) AS MORADA, CONCAT(MOR.MUN_DESCRICAO, ' ', PROV.PROV_DESCRICAO,' ',P.PAI_DESCRICAO) AS LOCALIDADE,
E.ENT_TELEFONE, E.ENT_EMAIL, E.ENT_RUA, E.ENT_BAIRRO, FIL_CATEGORIA FROM GER_ENTIDADE E 

INNER JOIN SIS_EMPRESA ESIS ON ESIS.FIL_CODIGO = E.ENT_CODIGO
LEFT JOIN GER_MUNICIPIO MOR ON MOR.MUN_CODIGO = E.ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA PROV ON PROV.PROV_CODIGO = MOR.MUN_CODIGO_PROVINCIA
LEFT JOIN GER_PAIS P ON P.PAI_CODIGO = PROV.PROV_CODIGO_PAIS
WHERE ESIS.FIL_CODIGO_EMPRESA=EMPRESA;
ELSE
SELECT E.ENT_CODIGO,  FIL_NOME_COMERCIAL, 
CONCAT(E.ENT_BAIRRO,' ',E.ENT_RUA) AS MORADA, CONCAT(MOR.MUN_DESCRICAO, ' ', PROV.PROV_DESCRICAO,' ',P.PAI_DESCRICAO) AS LOCALIDADE,
E.ENT_TELEFONE, E.ENT_EMAIL, E.ENT_RUA, E.ENT_BAIRRO, FIL_CATEGORIA FROM GER_ENTIDADE E 

INNER JOIN SIS_EMPRESA ESIS ON ESIS.FIL_CODIGO = E.ENT_CODIGO
LEFT JOIN GER_MUNICIPIO MOR ON MOR.MUN_CODIGO = E.ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA PROV ON PROV.PROV_CODIGO = MOR.MUN_CODIGO_PROVINCIA
LEFT JOIN GER_PAIS P ON P.PAI_CODIGO = PROV.PROV_CODIGO_PAIS
WHERE ESIS.FIL_CODIGO_EMPRESA IS NOT NULL;
END IF;


END
;;
