BEGIN
	#Routine body goes here...

SELECT E.ENT_CODIGO, E.ENT_BAIRRO, E.ENT_DATA_NASCIMENTO, E.ENT_EMAIL, IFNULL(E.ENT_LOCAL_MORADA, -1) AS ENT_LOCAL_MORADA, FIL_NOME_COMERCIAL,
MOR.MUN_DESCRICAO AS MORADA, PROV.PROV_DESCRICAO, IFNULL(PROV_CODIGO, -1) AS PROV_CODIGO, E.ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, 
ENT_TELEFONE_FAX, E.ENT_RUA, E.ENT_NOME_COMPLETO, E.ENT_IDENTIFICACAO, IFNULL(E.ENT_CODIGO_DOCUMENTO, -1) AS ENT_CODIGO_DOCUMENTO, 
FIL_CODIGO_EMPRESA, ENT_FOTOGRAFIA_PATH, FIL_CATEGORIA, ENT_WEBSITE, FIL_SUJEITO_PASSIVO, FIL_RETENCAO_FONTE 
FROM GER_ENTIDADE E  
INNER JOIN SIS_EMPRESA ESIS ON ESIS.FIL_CODIGO = E.ENT_CODIGO
LEFT JOIN GER_MUNICIPIO MU ON MU.MUN_CODIGO = E.ENT_LOCAL_NASCIMENTO
LEFT JOIN GER_MUNICIPIO MOR ON MOR.MUN_CODIGO = E.ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA PROV ON PROV.PROV_CODIGO = MOR.MUN_CODIGO_PROVINCIA
LEFT JOIN GER_PAIS P ON P.PAI_CODIGO = E.ENT_CODIGO_PAIS
WHERE E.ENT_CODIGO = CODIGO;

END
;;
