SELECT E.ENT_CODIGO, E.ENT_DATA_NASCIMENTO, F.FIL_RAZAO_SOCIAL, ENT_FOTOGRAFIA, E.ENT_IDENTIFICACAO, E.ENT_LOCAL_MORADA,
CONCAT(MOR.MUN_DESCRICAO, " ",PROV.PROV_DESCRICAO) AS MORADA, E.ENT_BAIRRO, E.ENT_RUA, E.ENT_TELEFONE, E.ENT_TELEFONE_ALTERNATIVO, 
E.ENT_TELEFONE_FAX, E.ENT_EMAIL, E.ENT_WEBSITE, ENT_FOTOGRAFIA_PATH, IFNULL(PROV_CODIGO, -1) AS PROV_CODIGO, FIL_CATEGORIA FROM GER_ENTIDADE E 
INNER JOIN SIS_EMPRESA F ON F.FIL_CODIGO = E.ENT_CODIGO
LEFT JOIN GER_MUNICIPIO MU ON MU.MUN_CODIGO = E.ENT_LOCAL_NASCIMENTO
LEFT JOIN GER_MUNICIPIO MOR ON MOR.MUN_CODIGO = E.ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA PROV ON PROV.PROV_CODIGO = MOR.MUN_CODIGO_PROVINCIA
LEFT JOIN GER_PAIS P ON P.PAI_CODIGO = E.ENT_CODIGO_PAIS
WHERE FIL_CODIGO_EMPRESA IS NULL ORDER BY FIL_CODIGO;

END
;;
