DROP PROCEDURE IF EXISTS `stp_SIS_FORMULARIO_PERMISSAO_CORRIGIR`;
delimiter ;;
CREATE PROCEDURE `stp_SIS_FORMULARIO_PERMISSAO_CORRIGIR`(PERFIL INT,
TAG INT)
BEGIN
DECLARE CONTADOR INT DEFAULT 0;
DECLARE INDICE INT DEFAULT 0;
DECLARE FORMULARIO INT DEFAULT 0;


SELECT COUNT(*) INTO CONTADOR FROM SIS_FORMULARIO F
INNER JOIN SIS_FORMULARIO_PERMISSAO FP ON FP.FORM_PERM_CODIGO_FORMULARIO = F.FORM_CODIGO
INNER JOIN SIS_PERFIL P ON P.PER_CODIGO = FP.FORM_PERM_CODIGO_PERFIL WHERE
FP.FORM_PERM_ACESSO=1 AND FP.FORM_PERM_CODIGO_PERFIL=PERFIL AND FORM_TAG=TAG;

IF(CONTADOR = 1) THEN
SELECT F.FORM_CODIGO, F.FORM_INDICE INTO FORMULARIO, INDICE FROM SIS_FORMULARIO F
INNER JOIN SIS_FORMULARIO_PERMISSAO FP ON FP.FORM_PERM_CODIGO_FORMULARIO = F.FORM_CODIGO
INNER JOIN SIS_PERFIL P ON P.PER_CODIGO = FP.FORM_PERM_CODIGO_PERFIL WHERE
FP.FORM_PERM_ACESSO=1 AND FP.FORM_PERM_CODIGO_PERFIL=12 ORDER BY FORM_TAG, FORM_INDICE;

IF(INDICE = 0) THEN
 UPDATE SIS_FORMULARIO_PERMISSAO SET FORM_PERM_ACESSO=1, FORM_PERM_ALTERACAO=1, FORM_PERM_CONSULTA=1,
 FORM_PERM_EXCLUSAO=1, FORM_PERM_IMPRESSAO=1, FORM_PERM_INCLUSAO=1 WHERE FORM_PERM_CODIGO_FORMULARIO = v_FORMULARIO
 AND FORM_PERM_CODIGO_PERFIL=PERFIL;
END IF;

END IF;
END
;;
