delimiter ;;
CREATE PROCEDURE `stp_SIS_FORMULARIO_PERMISSAO_OBTERPORMODULO`(PERFIL INT,
MODULO INT)
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.

    -- Insert statements for procedure here
    INSERT INTO SIS_FORMULARIO_PERMISSAO(FORM_PERM_CODIGO_FORMULARIO, FORM_PERM_CODIGO_PERFIL, FORM_PERM_INCLUSAO, 
    FORM_PERM_ALTERACAO, FORM_PERM_EXCLUSAO,  FORM_PERM_CONSULTA, FORM_PERM_IMPRESSAO, FORM_PERM_ACESSO)
    SELECT FORM_CODIGO, PERFIL, 0,0,0,0,0,0 FROM SIS_FORMULARIO WHERE 
    FORM_CODIGO NOT IN (SELECT FORM_PERM_CODIGO_FORMULARIO FROM SIS_FORMULARIO_PERMISSAO 
    WHERE FORM_PERM_CODIGO_PERFIL = PERFIL);
 
	 SELECT P.*, F.FORM_TITULO FROM SIS_FORMULARIO_PERMISSAO P
	 RIGHT JOIN SIS_FORMULARIO F ON P.FORM_PERM_CODIGO_FORMULARIO = F.FORM_CODIGO
	 INNER JOIN SIS_PERFIL C ON P.FORM_PERM_CODIGO_PERFIL = C.PER_CODIGO
	 INNER JOIN SIS_MODULO M ON F.FORM_CODIGO_MODULO = M.MOD_CODIGO	 WHERE
	 FORM_PERM_CODIGO_PERFIL = PERFIL AND F.FORM_CODIGO_MODULO=MODULO
	 ORDER BY F.FORM_TAG, F.FORM_INDICE ASC;
END
;;
